    //Banner Authentication with LDAP - middlware auth

    var url = $rootScope.middlewareServerUrl + "/bannermobile2/services/authenticate/login";
    var data = {};
    if (window.device) {
        data = "username=" + uname + "&password=" + password;
    } else {
        url = "/websimulator/json?url=" + url;
        data = {
            method: "POST",
            body: "username=" + uname + "&password=" + password
        };
    }
    var authUrl = url;
    var authData = data;
    $rootScope.generateST = function(tgt, service, callback) {
        //Bypassing the ST as there is no service ticket required here..
        callback(tgt);
    };

    $rootScope.postAPI = function(endpoint, postdata, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "POST";
            var proxyData = postdata;
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + serviceurl;
                proxyMethod = "POST";
                proxyData = {
                    method: "POST",
                    body: postdata
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        $rootScope.getAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };

    $rootScope.getAPI = function(endpoint, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "GET";
            var proxyData = {};
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                proxyMethod = "POST";
                proxyData = {
                    method: "GET"
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        $rootScope.getAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };




    $.blockUI({
        message: '<div style="margin: 2px; vertical-align: middle; display: inline-block"><i class="icon-cog icon-spin icon-4x"></i><h3>Authenticating</h3></div>'
    });

    $http.post(url, data).success(function(data, status, headers, config) {
        success(data);
        $.unblockUI();
    }).error(function(data, status, headers, config) {
        error(data);
        $.unblockUI();
    });

}

pages.pageprocessor:
function pageprocessorcluHome(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
 function Ctrl($scope)
{
    $scope.date = new Date();
}
  if (window.device && !$rootScope.loggedIn) {
    navigator.notification.alert('Please login to access this applet.', null, 'Need Authentication', 'Ok');
    $location.path("/app/SignIn279/SignIn279");
    return;
  }

  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  var gatewayUrl = $rootScope.GatewayUrl;

  $scope.loadAcademicCredits = function() {
    $rootScope.callAPI('/unifyedcladapter/academic-credits/' + $.jStorage.get("userCluEduPersonID"), 'GET', '', function(res) {
      if (res) {
        $scope.todaySchedule = res.data;
        var todaydate = new Date();
        todaydate.setHours(0, 0, 0, 0);
        $scope.userSch = {
          "AcademicTerms": []
        };
        try {
          for (var i = 0; i < $scope.todaySchedule.AcademicTerms.length; i++) {
            for (var j = 0; j < $scope.todaySchedule.AcademicTerms[i].AcademicCredits.length; j++) {
              if ($scope.todaySchedule.AcademicTerms[i].AcademicCredits[j].SectionId && $scope.todaySchedule.AcademicTerms[i].AcademicCredits[j].academicSection && $scope.todaySchedule.AcademicTerms[i].AcademicCredits[j].academicSection.Meetings) {
                if ($scope.todaySchedule.AcademicTerms[i].AcademicCredits[j].academicSection.Meetings.length > 0 && new Date($scope.todaySchedule.AcademicTerms[i].AcademicCredits[j].academicSection.Meetings[0].EndDate) >= todaydate && new Date($scope.todaySchedule.AcademicTerms[i].AcademicCredits[j].academicSection.Meetings[0].StartDate) <= todaydate) {
                  if ($scope.todaySchedule.AcademicTerms[i].AcademicCredits[j].academicSection.Meetings[0].Days.indexOf(todaydate.getDay().toString()) != -1) {
                    $scope.userSch.AcademicTerms.push($scope.todaySchedule.AcademicTerms[i].AcademicCredits[j]);
                  }
                }
              }
            }
          }
        } catch (e) {
          alert(e)
        }
        if (!$scope.userSch.AcademicTerms.length) {
          $("#userSchLoader").html("<div class='proximaSemiBoldFont font18 colorDark marginBottom10px cardTextOverflow'>You have no schedule for today</div>");
        } else {
          $scope.userSch.AcademicTerms.sort(function(a, b) {
            return new Date(a.academicSection.Meetings[0].StartTime).getTime() - new Date(b.academicSection.Meetings[0].StartTime).getTime();
          });

          $rootScope.userSch = $scope.userSch;
          setTimeout(function() {
            var swiper = new Swiper('.todaysSchedule', {
              loop: false,
              grabCursor: true,
              slidesPerView: 'auto',
              slidesPerSlide: 1,
              shortSwipes: true
            });
          }, 100);
        }
        $scope.$apply();
      } else {
        $("#userSchLoader").html("<div class='proximaSemiBoldFont font18 colorDark marginBottom10px cardTextOverflow'>You have no schedule for today</div>");
      }
    });
  }


  if ($rootScope.userSch) {
    $scope.userSch = $rootScope.userSch;
    setTimeout(function() {
      var swiper = new Swiper('.todaysSchedule', {
        loop: false,
        grabCursor: true,
        slidesPerView: 'auto',
        slidesPerSlide: 1,
        shortSwipes: true
      });
    }, 1000);
  } else {
    $scope.loadAcademicCredits();
  }



  try {
    var hostUrl = pagedef.bbHost + "/learn";

    $scope.parseJsonData = function(data) {
      if (data && typeof data == "string") {
        data = JSON.parse(data);
      }
      return data;
    }

    $scope.userToDos = false;
    $scope.getCourseName = function(id) {
      var getCrsName = "/unifyedbbadapter/?route=courses/" + id;
      $rootScope.isblocking = true;
      return new Promise(function(resolve, reject) {
        $rootScope.callAPI(getCrsName, 'GET', '', function(res) {
          var data = res.data;
          resolve(data.name);
        });
      });
    }

    $scope.loadToDos = function(id) {
      return new Promise(function(resolve, reject) {
        var toDoUrl = "/unifyedbbadapter/?route=courses/" + id + "/gradebook/columns";
        $rootScope.isblocking = true;
        $rootScope.callAPI(toDoUrl, 'GET', '', function(res) {
          try {
            var data = res.data;
            for (var i = 0; i < data.results.length; i++) {
              if (data.results[i].grading && data.results[i].grading.due && new Date() <= new Date(data.results[i].grading.due)) {
                data.results[i].crsId = id;
                if (!$scope.userToDos) {
                  $scope.userToDos = [];
                }
                $scope.userToDos.push(data.results[i]);
              }
            }
          } catch (e) {
            //alert(e);
          }
          resolve(data.results);
        });
      });
    }

    $scope.loadCourses = function() {
      var bbCoursesUrl = "/unifyedbbadapter/?route=users/userName:" + $.jStorage.get("unifyedusername").split('@')[0] + "/courses"
      var promiseArray = [];
      $rootScope.isblocking = true;
      $rootScope.callAPI(bbCoursesUrl, 'GET', '', function(res) {
        var data = res.data;
        for (var i = data.results.length - 1; i >= 0; i--) {
          promiseArray.push($scope.loadToDos(data.results[i].courseId));
        }
        Promise.all(promiseArray).then(function(values) {
          if (!$scope.userToDos) {
            $("#userToDosLoader").html("<div class='proximaSemiBoldFont font18 colorDark marginBottom10px cardTextOverflow'>No Assignments Due</div>");
            return;
          }
          var getCrsNamePromise = [];
          for (var i = 0; i < $scope.userToDos.length; i++) {
            getCrsNamePromise.push($scope.getCourseName($scope.userToDos[i].crsId));
          }
          Promise.all(getCrsNamePromise).then(function(values) {
            for (var i = 0; i < values.length; i++) {
              $scope.userToDos[i].crsName = values[i];
            }
            var today = moment(new Date()).format('L');
            var tomDate = moment(new Date()).add(1, 'days').format('L');
            for (var i = 0; i < $scope.userToDos.length; i++) {
              $scope.userToDos[i].day = null;
              var dueDate = new Date($scope.userToDos[i].grading.due);
              var dueDay = dueDate.getDate();
              var dueYear = dueDate.getFullYear();
              var duemonth = dueDate.getMonth() + 1;
              var newdueDate = duemonth + '/' + dueDay + '/' + dueYear;
              var comdueDate = moment(new Date(newdueDate)).format('L')
              if (comdueDate == today) {
                $scope.userToDos[i].day = 'today'
              } else if (comdueDate == tomDate) {
                $scope.userToDos[i].day = 'tomorrow'
              } else if (comdueDate < today) {
                $scope.userToDos[i].day = 'pastDate'
              } else {
                $scope.userToDos[i].day = 'futuredateDate'
              }
            }
            $rootScope.userToDos = $scope.userToDos;
            $scope.$apply();
            var swiper = new Swiper('.toDos', {
              loop: false,
              grabCursor: true,
              slidesPerView: 'auto',
              slidesPerSlide: 1,
              shortSwipes: true
            });
          });
        }).catch(function(e) {
          console.log(e);
        });
      });
    }

    //$scope.bbAPI($scope.loadCourses,bbCoursesUrl);
    if ($rootScope.userToDos) {
      $scope.userToDos = $rootScope.userToDos;
      setTimeout(function() {
        var swiper = new Swiper('.toDos', {
          loop: false,
          grabCursor: true,
          slidesPerView: 'auto',
          slidesPerSlide: 1,
          shortSwipes: true
        });
      }, 1000);
    } else {
      $scope.loadCourses();
    }


  } catch (e) {
    alert(e)
  }

  /***********Hub Events *****************/
  var months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
  /*ajax start*/
  var url = "https://earth.callutheran.edu/feeds/rss/events/student-calendar.atom";

  if ($rootScope.News343feeds) {
    $scope.feeds = $rootScope.News343feeds;
    setTimeout(function() {
      var swiper = new Swiper('.hubEvents', {
        loop: false,
        grabCursor: true,
        slidesPerView: 'auto',
        slidesPerSlide: 1,
        shortSwipes: true
      });
    }, 1000);
  } else {
    var xhr = $.ajax({
      url: url,
      dataType: "xml",
      async: true,
      data: {},
      success: function(resp) {
        var results = $.xml2json(resp);
        var feeds = results.entry;
        feeds.sort(function(a, b) {
          if (b.dtstart > a.dtstart)
            return -1;
          if (b.dtstart < a.dtstart)
            return 1;
          return 0;
        });
        $scope.feeds = [];
        var today = moment(new Date()).format('L');
        var td = new Date();
        var tomDate = moment(new Date()).add(1, 'days').format('L');
        for (var i = 0; i < feeds.length; i++) {
          feeds[i].day = null;
          var publishedDate = new Date(feeds[i].dtstart);

          if (publishedDate.setHours(0, 0, 0, 0).valueOf() >= td.setHours(0, 0, 0, 0).valueOf()) {
            var publishDay = publishedDate.getDate();
            var publishYear = publishedDate.getFullYear();
            var publishmonth = publishedDate.getMonth() + 1;
            var newPublishDate = publishmonth + '/' + publishDay + '/' + publishYear;
            var comPublishDate = moment(new Date(newPublishDate)).format('L')
            if (comPublishDate == today) {
              feeds[i].day = 'today'
            } else if (comPublishDate == tomDate) {
              feeds[i].day = 'tomorrow'
            } else if (comPublishDate < today) {
              feeds[i].day = 'pastDate'
            } else {
              feeds[i].day = 'futuredateDate'
            }
            $scope.feeds.push(feeds[i]);
          }

        }



        $rootScope.News343feeds = $scope.feeds;
        $scope.$apply();
        var swiper = new Swiper('.hubEvents', {
          loop: false,
          grabCursor: true,
          slidesPerView: 'auto',
          slidesPerSlide: 1,
          shortSwipes: true
        });
        $.unblockUI();
      },
      error: function(e) {
        // alert("Error : " + e);
        $.unblockUI();
        alert("error")
      }
    });



  }

  $scope.openFeed = function(index) {
    $rootScope.News343feedDetails = $scope.feeds[index];
    $rootScope.News343index = index;
    $scope.content = $rootScope.News343feedDetails.content;
    $location.path("/app/ArtsEvents/ArtsEventsDetails");
  };

  $scope.trustHTML = function(html) {
    if (html.indexOf('meta') != -1) {
      return '';
    } else {
      try {
        var i = html.indexOf('<script');
        var j = html.indexOf('</script');
        console.log(i);
        console.log(j);
        if (i != -1 && j != -1) {
          html = html.substr(0, i) + " " + html.substr(j + 9);
        }
      } catch (e) {
        console.log(e)
      }
      console.log(html);
      var data = "<div>" + html + "</div>";
      return $sce.trustAsHtml(data);
      console.log(data);

    }
  }

  $scope.getFeedDate = function(f) {
    return f.date.getDate();
  };

  $scope.getFeedMonth = function(f) {
    return months[f.date.getMonth()];
  };
  $scope.date = new Date();

  $scope.getFeedTime = function(f) {
    var date = f.date.toString();
    date = date.split(" ")[4];
    return $scope.tConvert(date);
  };

  $scope.tConvert = function(time) {
    time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

    if (time.length > 1) {
      time = time.slice(1);
      time[5] = +time[0] < 12 ? ' AM' : ' PM';
      time[0] = +time[0] % 12 || 12;
    }
    return time.join('');
  }

  $scope.backgroundImage = function(feed) {
    var imgUrl = feed.enclosure.url;
    var imgWidth = imgUrl.substring(imgUrl.lastIndexOf("_") + 1, imgUrl.lastIndexOf(".jpg"));
    imgWidth = imgWidth + 'px';
    return {
      'background': 'url(' + imgUrl + ') no-repeat',
      'background-size': 'cover',
      'height': '180px',
      'width': imgWidth
    }
  }
  $scope.trusthtml = function(data) {
    data = data.replace('"', '').replace('"', '');
    return $sce.trustAsHtml(data);
  }
}

