function pageprocessorToDos(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location, services, studioParam) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.studioParam = studioParam;

  setTimeout(function() {
    var time = new Date().toUTCString();
    $(".currentTime").text(time);
    $('.todocheck:checkbox').change(function() {
      if ($(this).is(":checked")) {
        $(this).parent().parent().removeClass("task-due");
        $(this).parent().parent().addClass("task-success");
        $(this).parent().siblings('.tasktitle').text("Completed");
        $(this).parent().siblings('.currentTime').show();
      }
      else {
        $(this).parent().parent().removeClass("task-success");
        $(this).parent().parent().addClass("task-due");
        $(this).parent().siblings('.tasktitle').text("Due");
        $(this).parent().siblings('.currentTime').hide();
      }
    });
  }, 1000)

}

function pageprocessorLaundry7(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  var countDownTimer = function(countDownDate, id){

	var x = setInterval(function() {

    // Get todays date and time
    var now = new Date().getTime();
    
    // Find the distance between now and the count down date
    var distance = countDownDate - now;
    
    // Time calculations for days, hours, minutes and seconds
    var days = Math.floor(distance / (1000 * 60 * 60 * 24));
    var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((distance % (1000 * 60)) / 1000);
    
    // Output the result in an element with id="demo"
    document.getElementById(id).innerHTML = minutes + ": " + seconds + "s ";
    
    // If the count down is over, write some text 
    if (distance < 0) {
        clearInterval(x);
        document.getElementById(id).innerHTML = "EXPIRED";
    }
}, 1000);
	

}
// Update the count down every 1 second

// Set the date we're counting down to
    var now = new Date().getTime();
var countDownDate = now + 565000000;
var countDownDate1 = now + 466000000;
var countDownDate2 = now + 766000000;
var countDownDate3 = now + 366000000;
var countDownDate3 = now + 786000000;

 countDownTimer(countDownDate, 'active');
 countDownTimer(countDownDate1, 'active1');
 countDownTimer(countDownDate2, 'active2');
 countDownTimer(countDownDate3, 'active3');
 countDownTimer(countDownDate3, 'active4');
}

