    //console.log(c);
    return _.intersection($scope.userWhatsUpAdminCategoryIds, c).length;
  }
  $scope.closeyoutube = function() {

    $scope.youtubeContainer = false;

  };
  $scope.postComments = function(id) {
    try {
      //console.log(id);

      var comment = $("#commentBox" + id).val();
      if (comment.length != 0) {
        /*  var index = $scope.posts.findIndex(function(x) {
         return x.id == id
         });*/

        var index = -1;
        $scope.posts.some(function(el, i) {
          if (el.id == id) {
            index = i;
            return true;
          }
        });
        try {
          $rootScope.isblocking = true;
          var feedUrl = "/unifydwhatsup/comment";
          var data = {
            "postId": id,
            "username": $rootScope.user.firstName + " " + $rootScope.user.lastName,
            "userimage": $rootScope.user.profileImage,
            "userRole": $rootScope.user.role,
            "comment": comment
          }
          $rootScope.callAPI(feedUrl, 'POST', data, function(data) {
            if (!data.data.error) {
              $scope.posts[index].comment += 1;
              $scope.posts[index].comments.push(data.data);
              if (!$scope.posts[index].showViewAllComment) {
                $scope.posts[index].limitComment = $scope.posts[index].comment;
              }
              $scope.$apply();
            }
          });
          //console.log($scope.posts);
        } catch (e) {
          alert(e)
        }
        $("#commentBox" + id).val("");
        $("#commentBox" + id).closest(".media-body").find(".replybtn").removeClass('themeText').addClass('replybtnText');
        //$(this).closest(".media-body").find(".addCommentInput").val("");
        //$("#commentBox"+id).closest('replybtn').removeClass('yellowText').addClass('replybtnText');
      }
    } catch (e) {
      alert(e)
    }
  }
  $window.onclick = function(event) {


    console.log(" check for flag");
    if ($scope.showCreatePost) {


      if ($scope.postImage.length == 0 && $('#postTitle').val() == '' && !$scope.youtubeContainer && $scope.postTags.length == 0)
        $scope.showCreatePost = false;
      $scope.$apply();

    }

  }
  $scope.trustSrc = function(videoID) {
    return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0");
  };

  $scope.trusthtml = function(data) {
    return $sce.trustAsHtml(data);
  }
  $scope.loadYTFrame = function($event) {


    var elem = $event.currentTarget;
    var newEle = angular.element("<iframe frameborder='0'></iframe>");
    var embed = "https://www.youtube.com/embed/ID";
    newEle.attr("src", embed.replace("ID", angular.element($event.currentTarget).attr('data-id')));
    var e = $compile($(newEle))($scope);
    $(elem).replaceWith($(e));
  }
}

function pageprocessorConnectPage3(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
