            //alert(JSON.stringify(data.data.content));
            $scope.p.like = data.data.content;
            $scope.hideLikesLoader = false;

            var commentUrl = "/unifydwhatsup/comment/" + $rootScope.postDetails.id + "?size=3&sort=createdDate,DESC";
            $rootScope.callAPI(commentUrl, 'GET', '', function(data) {
                $scope.p.comments = data.data.content.reverse();
