            /*$('#likeUnlike'+index).addClass('pulse animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {
                $(this).removeClass('pulse animated');
            });*/

            if ($scope.p.liked) {

                var feedUrl = "/unifydwhatsup/like/" + $scope.p.id;
                $rootScope.callAPI(feedUrl, 'DELETE', '', function(data) {
                    var likeIndex = $scope.p.like.findIndex(function(x) {
                        return x.id == id
                    });
                    $scope.p.like.splice(likeIndex, 1);
                    $scope.p.liked = false;
                    $scope.p.likes -= 1;
                });
