    var month = t2.substring(0, 1).toUpperCase() + t2.substring(1);
    return month + " " + t1 + ", " + t3;
  }

  $scope.toggleClick = function() {
    $('#dropdownMenu1').toggleClass('dropdownSelectBackground');
    $('#dropArrow').toggleClass('rotateImage');
  }


  function initApplet() {
    $rootScope.getAPI(config, aidYearDropdownEndpoint, function(data) {
      setTimeout(function() {
        if (data) {
          var aidYears = data.finaidyears;
          $scope.finaidyears = [];
          if (yearsToShow > 0) {
            angular.forEach(aidYears, function(value, key) {
              if (yearsToShow >= key || yearsToShow == 0) {
                $scope.finaidyears.push(value);
              }
            });
          } else {
            $scope.finaidyears = aidYears;
          }
          if ($scope.finaidyears && $scope.finaidyears.length > 0) {
            $scope.selyearDesc = $scope.finaidyears[0].DESCRIPTION;
            $scope.selyear = $scope.finaidyears[0].AIDY;
            $scope.yearselect(0);
            $scope.toggleClick();
          }


        } else {
          showAlert("No information is available for your account.")
        }
      }, 5)
    });
  }

  // Call the applet initialization code
  initApplet();

}

