    $scope.Enrollment = false;
    $scope.Schedule = false;
    $scope.statusBar = false
    $scope.landingPage = false
    $scope.summaryStatus = false
    $scope.viewLessDetails = false;
    $scope.viewMoreDetails = false;
  }


  $scope.termselect = function(index) {
    $.blockUI();
    initAll();
    $scope.landingPage = true;
    $scope.listdata = null;
    var t = $scope.dropdown[index];
    $scope.selterm = t.TERM_DESC;
    $scope.seltermCode = t.TERM_CODE;
    /*$('#clickName').blur();*/
    if (!$scope.selterm) {
      $scope.listdata = [];
      return;
    }

    $rootScope.getAPI(config, listendpoint + t.TERM_CODE, function(data) {
        if (data.p_out[0].summary && typeof data.p_out[0].summary == "string") {
            data.p_out[0].summary = JSON.parse(data.p_out[0].summary);
        }
      $scope.listdata = data.p_out[0].summary;

      if ($scope.listdata && $scope.listdata.length > 0 && $scope.listdata[0].error) {
        $scope.landingPage = true
        //showAlert($scope.listdata[0].error);
      } else {
        if ($scope.listdata) {
            $scope.landingPage = true
        } else {
            $scope.landingPage = false
        }
      }

    });
  }


  $scope.summaryClass = function() {
    if (!$scope.seltermCode || !$scope.detailData || !$scope.detailData.course) {
        return;
    }
    $scope.previousPageStatus = ($scope.General) ? "General" : ($scope.Enrollment) ? "Enrollment" : "Schedule"
    initAll();
    $scope.summaryStatus = true;
    $scope.viewLessDetails = true;
    var endpoint = summaryScheduleEndpoint + "/termCode=" + $scope.seltermCode + "&crn=" + $scope.detailData.course.crn;
    $scope.summarylist = [];
    $rootScope.getAPI(config, endpoint, function(data) {
        console.log("data.p_out[0]")
        if (data && data.p_out[0].summary) {
            if (typeof data.p_out[0].summary == "string") {
                var result = JSON.parse(data.p_out[0].summary);
                data.p_out[0].summary = result;
            }
            console.log("data.p_out[0].summary[0].error")
            console.log(data.p_out[0].summary[0].error)
            if (data.p_out[0].summary[0].error) {
                showAlert(data.p_out[0].summary[0].error);
            } else {
                $scope.summarylist = data.p_out[0].summary;
                if ($scope.summarylist[0].summclasslist.length == 0) {
                    showAlert("Class list is not available for the selected course.");
                }
            }
        }
    });    
  }

  $scope.viewMore = function() {
    $scope.viewLessDetails = false;
    $scope.viewMoreDetails = true;
  }

  $scope.viewLess = function() {
    $scope.viewLessDetails = true;
    $scope.viewMoreDetails = false;
  }

  $scope.backProcess = function() {
    $scope.changeStatus($scope.previousPageStatus)
  }

  $rootScope.back = function() {
    $scope.changeStatus($scope.previousPageStatus)
  }

  $scope.changeStatus = function(pageStatus) {

    initAll()
    switch (pageStatus) {

      case "General":
        $scope.General = true;
        $("#tab1").addClass("toolbar-column-active")
        $("#tab2").removeClass("toolbar-column-active")
        $("#tab3").removeClass("toolbar-column-active")
        break;
      case "Enrollment":
        $scope.Enrollment = true;
        $("#tab1").removeClass("toolbar-column-active")
        $("#tab2").addClass("toolbar-column-active")
        $("#tab3").removeClass("toolbar-column-active")
        break;
      case "Schedule":
        $scope.Schedule = true
        $("#tab1").removeClass("toolbar-column-active")
        $("#tab2").removeClass("toolbar-column-active")
        $("#tab3").addClass("toolbar-column-active")
        break;
    }
    $scope.statusBar = true
  }

  $scope.showDetails = function(value) {
    $scope.landingPage = false;
    $scope.changeStatus("General");
    $scope.detailData = value
  }

  function initApplet() {
    $scope.dropdown = [];
    $scope.indexTerm = $scope.indexTerm != undefined ? $scope.indexTerm : 0;

    $.blockUI();
    $rootScope.getAPI(config, dropdownendpoint, function(data) {
      $scope.dropdown = data.termdropdown;
      if ($scope.dropdown.length >= 1) {
        $scope.termselect($scope.indexTerm)
        initAll();
      } else {
        $scope.showsummary = false;
      }
    });

  }
  initApplet();
}

