  /*fix message count issue */
  function lineBreakCount(str){
    /* counts \n */
    try {
       var a = str.match(/[^\n]*\n[^\n]*/gi).length;
       var b = str.match(/[^\r]*\r[^\r]*/gi).length;
      return(a+b);
    } catch(e) {
      return 0;
    }
  }
     

