    //  console.log($scope.selStatus);
    var channelStr = $scope.selChannels.toString();
    var status = "";
    if ($scope.selStatus && $scope.selStatus.length == 1) {
      status = ($scope.selStatus.indexOf("Sent") == -1) ? "Scheduled" : "Sent";
    }
    channelStr = (channelStr) ? "&channels=" + channelStr : "";
    status = (status) ? "&status=" + status : "";
    if (channelStr || status) {
      $scope.searchCase = 1;
    }

    var url = '/unifydmessaging/feed/search?sort=createdDate,DESC&size=5&page=' + page + channelStr + status;
    $.blockUI();
    $rootScope.callAPI(url, 'GET', {}, function(res) {
      if (res != undefined && res) {
        $scope.page = res.data.number;
        $scope.last = res.data.last;


        //  console.log("1",$scope.setMess);
        // console.log("2",$scope.messagingList);
        if (!$scope.messagingList) {
          $scope.messagingList = res.data.content;
          // console.log("3",$scope.setMess);
          //  console.log("4",$scope.messagingList);
          if ($scope.messagingList.length > 0 && $scope.messagingList != undefined) {
            //    console.log("5",$scope.setMess);
            //console.log("6",$scope.messagingList);
            $.unblockUI();
            $scope.messagingList = $scope.messagingList.map(function(value, key) {
              value.shortMessage = value.defaultMessage.slice(0, 240);
              value.shortMessage = value.shortMessage.replace(/<br\/>/g, '\n');
              value.messageDetailLess = 0;
              if (!value.sent) {
                var today = new Date();
                var scheduleTime = new Date(value.scheduleTime);
                var timein12H, formattedDateStr;
                if (today > scheduleTime) {
                  if (value.createdDate == value.updatedDate) {
                    var sentDate = new Date(value.createdDate);
                  } else {
                    var sentDate = new Date(value.updatedDate);
                  }
                  timein12H = parseDateTime(sentDate).replace(":00", "").replace(":0", "");
                  formattedDateStr = "Sent - " + sentDate.getDate() + " " + MONTHS[sentDate.getMonth()] + ", " + sentDate.getFullYear() + ", " + timein12H;
                  value.messageStatus = "Sent";
                  value.messageTitle = formattedDateStr;
