                  timein12H = parseDateTime(scheduleTime).replace(":00", "").replace(":0", "");
                  formattedDateStr = "Scheduled - " + scheduleTime.getDate() + " " + MONTHS[scheduleTime.getMonth()] + ", " + scheduleTime.getFullYear() + ", " + timein12H;
                  value.messageStatus = "Scheduled";
                  value.messageTitle = formattedDateStr;
                }
              } else {
                var sentDate = new Date(value.createdDate);
                timein12H = parseDateTime(sentDate).replace(":00", "").replace(":0", "");
                formattedDateStr = "Sent - " + sentDate.getDate() + " " + MONTHS[sentDate.getMonth()] + ", " + sentDate.getFullYear() + ", " + timein12H;
                value.messageStatus = "Sent";
                value.messageTitle = formattedDateStr;
              }
              return value;
            });
          } else {
            $scope.messagingList = [];
            $scope.setMess = 1;
            //   console.log("newtest", $scope.setMess);
            //  console.log("newtest1", $scope.messagingList);
            $.unblockUI();
          }
        } else {
          // console.log("loding................");
          $.unblockUI();
          $scope.messagingList = $scope.messagingList.concat(res.data.content);
          if ($scope.messagingList.length > 0) {
            console.log("7", $scope.setMess);
            console.log("8", $scope.messagingList);
            $scope.messagingList = $scope.messagingList.map(function(value, key) {
              value.shortMessage = value.defaultMessage.slice(0, 240);
              value.messageDetailLess = 0;
              if (value.scheduledMsg) {
                var today = new Date();
                var scheduleTime = new Date(value.scheduleTime);
                var timein12H, formattedDateStr;
                if (today > scheduleTime) {
                  var sentDate = new Date(value.createdDate);
                  timein12H = parseDateTime(sentDate).replace(":00", "").replace(":0", "");
                  formattedDateStr = "Sent - " + sentDate.getDate() + " " + MONTHS[sentDate.getMonth()] + ", " + sentDate.getFullYear() + ", " + timein12H;
                  value.messageStatus = "Sent";
                  value.messageTitle = formattedDateStr;
                } else {
                  timein12H = parseDateTime(scheduleTime).replace(":00", "").replace(":0", "");
                  formattedDateStr = "Scheduled - " + scheduleTime.getDate() + " " + MONTHS[scheduleTime.getMonth()] + ", " + scheduleTime.getFullYear() + ", " + timein12H;
                  value.messageStatus = "Scheduled";
                  value.messageTitle = formattedDateStr;
