    $scope.searchUser = function(page, event) {

      if (event) {
        event.stopPropagation();
        event.preventDefault();
      }

      if ($scope.userSearchEmail.length < 3)
        return;

      if (!page)
        page = 0;
      else
        page++;

      var userStr = $scope.userSearchEmail.toLowerCase();
      var url = '/unifydidentity/user/search/findByEmailStartingWith?sort=email,ASC&email=' + userStr + '&page=' + page;

      $rootScope.isblocking = true;
      $rootScope.callAPI(url, 'GET', {}, function(res) {
        $rootScope.isblocking = false;
        var data = res.data;
        $scope.userSearchResult.users = data['content'];
        $scope.userSearchResult.page = data['number'];
        $scope.userSearchResult.last = data['last'];
      })

    };


    $scope.searchUsersMatching = function() {
      var str = $scope.userSearchEmail;
      var queryParam = ($scope.external) ? "&external=external" : "";
      var url = "/unifydidentity/open/users?userId=" + str + queryParam;
      $rootScope.callAPI(url, 'GET', {}, function(res) {
        $scope.userSearchResult.users = (res && res.data) ? res.data : [];
        $scope.userSearchResult.last = true;
        $scope.userSearchResult.page = 1;
      })
    }


    $scope.validatePost = function() {


      if ($rootScope.selectedChannelsMessaging.length === 0) {
        $scope.showAlert('Select at least one channel.');
        return false;
      }

      if ($rootScope.selectedChannelsMessaging.indexOf('email') !== -1 && !$scope.title) {
        $scope.showAlert('Enter subject for the post.');
        return false;
      }

      if ($rootScope.selectedChannelsMessaging.indexOf('connect') !== -1 && !$scope.description && !$scope.postImage) {
        $scope.showAlert('Enter message or upload media for the post.');
        return false;
      }

      if (!$scope.description) {
        $scope.showAlert('Enter message for the post.');
        return false;
      }

      return true;

    };

    $scope.addUser = function(e, event) {
      event.stopPropagation();
      event.preventDefault();
      $rootScope.selectedUsersMessaging.push(($scope.external) ? e : e.email);
    };

    $scope.removeUser = function(index, e) {
      e.stopPropagation();
      e.preventDefault();
      $rootScope.selectedUsersMessaging.splice(index, 1);
    };


    setTimeout(function() {
      $(".expCollapse").click(function() {

        $header = $(this);
        //getting the next element
        $content = $header.next();
        //open up the content needed - toggle the slide- if visible, slide up, if not slidedown.

        $content.slideToggle(100, function() {
          //execute this after slideToggle is done
          //change text of header based on visibility of content div
          $header.text(function() {
            //change text based on condition
            return $content.is(":visible") ? "Collapse" : "See All";
          });
        });

      });

    }, 200);

    $scope.descriptionLength = 0;
    $scope.textChange = function() {
      $scope.descriptionLength = document.getElementById("messagedesc").value.length;
    };


    $scope.selectChannel = function(channel, e) {
      e.stopPropagation();
      e.preventDefault();

      var index = $rootScope.selectedChannelsMessaging.indexOf(channel['channelId']);
      if (index === -1) {
        $rootScope.selectedChannelsMessaging.push(channel['channelId'])
      } else {
        $rootScope.selectedChannelsMessaging.splice(index, 1);
      }
    };

    $scope.youtube_parser = function(url) {
      var regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#\&\?]*).*/;
      var match = url.match(regExp);
      return (match && match[7].length == 11) ? match[7] : false;
    };

    $scope.youtubeClick = function() {
      $scope.youtubeContainer = !$scope.youtubeContainer;
    };

    $scope.loadYoutubeImage = function() {
      $scope.youtubeurl = document.getElementById('youtubeurl').value;
      if ($scope.youtubeurl == '')
        return;
      var videoId = $scope.youtube_parser($scope.youtubeurl);

      if (!videoId) {
        $scope.showAlert("Invalid Youtube URL");

        return;
      }


      var videoUrl = 'https://www.googleapis.com/youtube/v3/videos?id=' + videoId + '&key=AIzaSyBhlEA5VL32Wtmoz72VEcG83CFmmP-f6lg&fields=items(snippet(title))&part=snippet'

      var option = {
        url: videoUrl
      };

      $rootScope.callOpenAPI(option, function(err, data) {
        $scope.youtubeClick();
        $scope.postImage = 'https://img.youtube.com/vi/' + videoId + '/hqdefault.jpg';
        $scope.imageSource = 'youtube';
        $('#previewVideo').attr('src', $scope.youtubeurl);
        $scope.videoId = videoId;
        $scope.postContentTitle = data['items'][0]['snippet']['title'];
      });

      //$('#postImageDiv').attr("src", $scope.postImage);
    };
    $scope.clearImage = function() {
      $scope.postImage = '';
      $scope.imageSource = '';
      $scope.videoId = '';
      $scope.postContentTitle = '';

    };
    $("#myPostImg").change(function() {
      if (this.files && this.files[0]) {
        var file = this.files[0];
        if (this.files[0].name.split('.').pop().toLowerCase() == 'png' || this.files[0].name.split('.').pop().toLowerCase() == 'jpg' || this.files[0].name.split('.').pop().toLowerCase() == 'jpeg') {


          var FR = new FileReader();
          FR.onload = function(e) {
            $scope.postImage = e.target.result;
            $scope.imageSource = 'camera';
            $('#postImageDiv').attr("src", $scope.postImage);
            $scope.postContentTitle = file.name;
            $scope.$apply();
          };
          FR.readAsDataURL(this.files[0]);
          $("#myPostImg")[0].value = '';
        } else {
          alert("Only png, jpg file types are allowed.");
        }
      }
    });

    function onFail(message) {
      //alert('Failed because: ' + message);
      $.unblockUI();
    }

    $scope.cameraSuccess = function(imageData) {
      $scope.postImage = "data:image/png;base64," + imageData;
      $scope.imageSource = 'camera';
      $('#postImageDiv').attr("src", $scope.postImage);
      //$("#postImg").attr('src', "data:image/png;base64," + imageData);
      $scope.$apply();
      $.unblockUI();
    }

    $scope.useCamera = function() {
      $.blockUI({
        message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
      });
      navigator.camera.getPicture($scope.cameraSuccess, onFail, {
        quality: 50,
        destinationType: Camera.DestinationType.DATA_URL
      });
    }

    $scope.useGallery = function() {
      $.blockUI({
        message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
      });
      navigator.camera.getPicture($scope.cameraSuccess, onFail, {
        quality: 50,
        destinationType: Camera.DestinationType.DATA_URL,
        sourceType: Camera.PictureSourceType.PHOTOLIBRARY,
        popoverOptions: new CameraPopoverOptions(300, 300, 100, 100, Camera.PopoverArrowDirection.ARROW_ANY)
      });
    }

    var ActionSheetSuccess = function(btn) {
      if (btn == 1) {
        $scope.useCamera();
      } else {
        $scope.useGallery();
      }
    };

    $scope.openCamera = function() {
      var options = {
        'androidTheme': window.plugins.actionsheet.ANDROID_THEMES.THEME_HOLO_LIGHT,
        'title': '',
        'buttonLabels': ['Take a photo', 'Choose from gallery'],
        'addCancelButtonWithLabel': 'Cancel'
      };
      window.plugins.actionsheet.show(options, ActionSheetSuccess);
    };

    $scope.uploadImage = function() {
      if (window.device) {
        if ($rootScope.updateMessage) {
          $scope.editSchduleMessageImage = 0;
        }
        $scope.openCamera();
      } else {
        //console.log('filechooser');
        try {
          if ($rootScope.updateMessage) {
            $scope.editSchduleMessageImage = 0;
          }
          document.getElementById("myPostImg").click();
        } catch (e) {
          alert(e);
        }

      }
    };

    try {
      var observe;
      if (window.attachEvent) {
        observe = function(element, event, handler) {
          element.attachEvent('on' + event, handler);
        };
      } else {
        observe = function(element, event, handler) {
          element.addEventListener(event, handler, false);
        };
      }

      $scope.init = function() {
        /*
         $(".messageText").on("keydown", "textarea", function () {
         autoheight(this);
         });
         */

        $(".messageText").keyup(function(e) {
          autoheight(this);
        });

        function autoheight(a) {
          if (!$(a).prop('scrollTop')) {
            do {
              var b = $(a).prop('scrollHeight');
              var h = $(a).height();
              $(a).height(h - 5);
            }
            while (b && (b != $(a).prop('scrollHeight')));
          };
          $(a).height($(a).prop('scrollHeight') + 0);
        }

        setTimeout(function() {
          $(".messageText").each(function(index) {
            autoheight(this)
          })
        }, 200);
      };
      $scope.init();
    } catch (e) {
      console.log(e)
    }


    $scope.trustSrc = function(url) {
      return $sce.trustAsResourceUrl('https://www.youtube.com/embed/' + $scope.youtube_parser(url));
    };

    $scope.showAlert = function(message) {
      if (window.device) {
        navigator.notification.alert(message, null, ' ', 'Ok');
      } else {
        //alert("Invalid youtube url.");
        $.alert({
          animation: 'none',
          title: '',
          content: message,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      }

    };

    $scope.editContent = function(channel) {

      $scope.editMode = !$scope.editMode;
      console.log("editmode=" + $scope.editMode);
      try {
        switch (channel) {
          case 'connect':
            $scope.editMessage = $('#connectMessage').val();
            break;
          case 'email':
            $scope.editMessage = $('#emailMessage').val();
            $scope.editTitle = $('#titleMessage').val();
            break;
          case 'sms':
            $scope.editMessage = $('#smsMessage').val();
            break;
        }
      } catch (e) {
        console.log(e);
      }

    };

    $scope.cancelContent = function(channel) {
      $scope.editMode = !$scope.editMode;
      if ($scope.editMessage == "~") {
        $scope.editMessage = $scope.description;
      }
      switch (channel) {
        case 'connect':
          $('#connectMessage').val($scope.editMessage);
          break;
        case 'email':
          $('#emailMessage').val($scope.editMessage);
          $('#titleMessage').val($scope.editTitle);
          break;
        case 'sms':
          $('#smsMessage').val($scope.editMessage);
          break;
      }
      $scope.init();
    };

    $scope.saveContent = function(channel) {

      switch (channel) {
        case 'connect':
          if (!$('#connectMessage').val()) {
            $scope.showAlert('Enter message for connect post.');
            return;
          }
          break;
        case 'email':
          if (!$('#emailMessage').val()) {
            $scope.showAlert('Enter message for email.');
            return;
          }
          if (!$('#titleMessage').val()) {
            $scope.showAlert('Enter title for email.');
            return;
          }
          $scope.title = $('#titleMessage').val();
          break;
        case 'sms':

          if (!$('#smsMessage').val()) {
            $scope.showAlert('Enter message for sms.');
            return;
          }

          break;
      }
      $scope.editMode = !$scope.editMode;

    };
    $scope.closeyoutube = function() {

      $scope.youtubeContainer = false;

    };
    $scope.scheduleListener = function() {
      if (!$scope.publishNow) {
        if (window.device) {
          var options = {
            date: new Date(),
            mode: 'dateTime',
            allowOldDates: false,
            minDate: ''
          };

          function onSuccess(date) {
            angular.element(document.querySelector('#datetimepicker11')).text(new Date(date.getFullYear(), date.getMonth(), date.getDate(), date.getHours(), date.getMinutes(), date.getSeconds(), date.getMilliseconds()));
          }

          function onError(error) {
            // alert('Error: ' + error);
          }
          datePicker.show(options, onSuccess, onError);

        } else {
          var d = new Date();
          $('#datetimepicker11').data("DateTimePicker").date(new Date(d.getFullYear(), d.getMonth(), d.getDate(), d.getHours(), d.getMinutes() + 30, d.getSeconds(), d.getMilliseconds()));
          $('#datetimepicker11').data("DateTimePicker").minDate(new Date(d.getFullYear(), d.getMonth(), d.getDate(), d.getHours(), d.getMinutes() + 30, d.getSeconds(), d.getMilliseconds()));
        }
      }
    }

  } catch (e) {
    alert(e)
  }
}

