function pageprocessorbbMyCourses9(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {



  if (!$rootScope.BB || !$rootScope.BB.ticket) {
  	$location.path('app/bbMyCourses9/bbMyCourses9Page5');
  	$scope.$apply();
  	return;
  }
  $rootScope.bbCourseApp = {
    init: false,
    pending: 0
  }

  if (!$rootScope.productConfig) {
    $rootScope.productConfig = {
      product: pagedef.product,
      middlewareServerUrl: pagedef.middlewareServerUrl
    };
  }



  var data1 = $compile($(pagedef.datatemplate))($scope);
