  $scope.selectCourse = function(course) {
    $rootScope.bbSelectedCourse = course;
    $location.path("app/bbMyCourses9/bbMyCourses9Page6");
  };

  $rootScope.bbCourseApp.pending++;

  $rootScope.BB.postAPI($rootScope.productConfig, "/bbmycourses", "", function(err, data) {

    $scope.bbmycourses = data.COURSES;
    var caldata = JSON.parse(data.CALENDAR_DATA);
    var getCalCourse = function(cid) {
      var obj = {};
      for (var i = 0; i < caldata.calendars.length; i++) {
        var cobj = caldata.calendars[i];
        if (cobj.id == cid) {
          obj = cobj;
          break;
        }
      }
      return obj;
    }

    $.each($scope.bbmycourses, function(i, val) {
      var calcrse = getCalCourse(val.TITLE.split(":")[0]);
      console.log("Cal crse : " + calcrse);
      val.CALDATA = calcrse;
    });
    console.log(caldata);
    $rootScope.bbCourseApp.init = true;
    $rootScope.bbCourseApp.pending--;
    $.unblockUI();
  });
}

