    $.blockUI();
    if ($scope.mailConfig.serviceAccount && $scope.mailConfig.serviceAccountPass) {
      // Call the o365 mail api directly
      getEmails(true);
    } else {
      // Get user password using clearpass api before calling mail api
      initIFrame();
    }
  }

  $scope.mailCountSelect = function(index) {
    $scope.selCount = $scope.mailCountDropdown[index];
    $.blockUI();
    if ($scope.mailConfig.serviceAccount && $scope.mailConfig.serviceAccountPass) {
      // Call the o365 mail api directly
      getEmails(true);
    } else {
      // Get user password using clearpass api before calling mail api
      initIFrame();
    }
  }

  $scope.refreshMail = function() {
    $.blockUI();
    if ($scope.mailConfig.serviceAccount && $scope.mailConfig.serviceAccountPass) {
      // Call the o365 mail api directly
      getEmails(true);
    } else {
      // Get user password using clearpass api before calling mail api
      initIFrame();
    }
  }

  var months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, "O365", null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: 'O365',
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      } else {
        alert(msg);
      }
    }
  }


  var iabRef;

  if (!$rootScope.webApp) {
    if (!$rootScope.username) {
      var jUser = $.jStorage.get("unifyedusername");
      if (jUser.indexOf('@') != -1) {
        jUser = jUser.substr(0, jUser.indexOf('@'));
      }
      $rootScope.username = jUser;
    }
    $rootScope.password = (!$rootScope.password) ? $rootScope.password : $.jStorage.get("unifyedpassword");
  }

  window.qlIframeOnload = function() {
    console.log("IFRAME loaded...");
    showQLSSOApps();
  }

  function fnGetClearpass(config, callBack) {
    if ($rootScope.password) {
      if (!$rootScope.webApp && !$rootScope.password) {
        showAlert("Error: User password is Blank, can't proceed.");
        return callBack({
          "err": "User password is Blank, can't proceed."
        });
      }
      return callBack(null, config);
    }
    //var clearpassEndpoint =  "https://qlsso.quicklaunchsso.com/admin/secured/" + qlTenantid + "/api/getClearPass";
    console.log("calling clearpass API for getting password:");
    var ssoEndpoint = $rootScope.qlssoUrl;
    if ($rootScope.qlssoUrl.substr($rootScope.qlssoUrl.length - 1) == "/") {
      ssoEndpoint = ssoEndpoint.substr(0, ssoEndpoint.length - 1);
    }
    var clearpassEndpoint = ssoEndpoint + "/admin/secured/" + $rootScope.qlTenantId + "/api/getClearPass";
    console.log("clearpassurl= " + clearpassEndpoint);
    $.ajax({
      url: clearpassEndpoint,
      //contentType: 'application/json;charset=UTF-8',
      crossDomain: true,
      dataType: 'jsonp',
      xhrFields: {
        withCredentials: true
      },
      success: function(data) {
        $rootScope.password = config.password = data;

        //$scope.$apply();
        return callBack(null, config);
      },
      error: function(xhr, status) {
        $.unblockUI();
        console.log("API ERROR:" + status);
        console.log(xhr)
      }
    });
  }

  function showQLSSOApps() {
    fnGetClearpass(config, function(err, config) {
      if (!$rootScope.password) {
        $.unblockUI();
        console.error("Couldnt retrieve password, returning");
        return;
      }
      getEmails(false);
    });
  }

  $.blockUI();
  var mailConfigEndpoint = '/unifydo365/mailconfiguration/search/findOneByTenantid?tenantid=' + $rootScope.user.tenant;
  $rootScope.callAPI(mailConfigEndpoint, 'GET', {}, function(res) {
    if (!res && !res.data && !res.data.tenantid) {
      showAlert("O365 setup not done, quitting !");
      return;
    }
    $scope.mailConfig = res.data;
    if ($scope.mailConfig.tenantid != $rootScope.user.tenant) {
      $.unblockUI();
      showAlert("Tenant id not matching, check the configuration.");
      return;
    }
    if (!$scope.mailConfig.serviceUrl) {
      $.unblockUI();
      showAlert("O365 mail service Url is missing !");
      return;
    }
    if (!$scope.mailConfig.domain) {
      $.unblockUI();
      showAlert("O365 mail domain is missing !");
      return;
    }
    if (!$scope.mailConfig.dtFormat) {
      $.unblockUI();
      showAlert("O365 mail date format is missing !");
      return;
    }
    if (!$scope.mailConfig.timeZone) {
      $.unblockUI();
      showAlert("O365 mail timezone is missing !");
      return;
    }
    if ($scope.mailConfig.serviceAccount && $scope.mailConfig.serviceAccountPass) {
      // Call the o365 mail api directly
      getEmails(true);
    } else {
      // Get user password using clearpass api before calling mail api
      initIFrame();
    }
  });


  function getEmails(isServiceValid) {
    var pass = (isServiceValid) ? "" : $rootScope.password;;
    var mailEndpoint = "/unifydo365/getEmail";
    var reqdata = {
      "tenantid": $rootScope.user.tenant,
      "email": $rootScope.user.email,
      "password": pass,
      "mailCount": $scope.selCount,
      "emailFilter": (($scope.selFilter == "All") ? 0 : 1)
    }
    $scope.userEmails = [];
    $rootScope.callAPI(mailEndpoint, 'POST', reqdata, function(res) {
      $.unblockUI();
      if (res && res.data) {
        if (res.errorFound) {          
          return;
        }
        $scope.userEmails = res.data.responseData;
        $scope.lastRefreshedDate = res.data.displayLastRefresh;
        if ($scope.lastRefreshedDate) {
          $scope.lastRefreshedDate = $scope.lastRefreshedDate.replace(/&nbsp;/gi, ' ');
        }
        var today = new Date();
        angular.forEach($scope.userEmails, function(value, key) {
          var dt = new Date(value.receivedDtFormatted);
          if (dt.getFullYear() == today.getFullYear() && dt.getMonth() == today.getMonth() && dt.getDate() == today.getDate()) {
            value.formattedDate = formatAMPM(dt);
          } else {
            value.formattedDate = dt.getDate() + " " + months[dt.getMonth()];
          }

        });

      } else {
        
        return;
      }
    });
  }


  function initIFrame() {
    var qlSecuredListApplUrl = $rootScope.qlssoUrl + "/admin/secured/" + $rootScope.qlTenantId + "/api/list/applications";
    console.log("SSO Url: " + qlSecuredListApplUrl);
    $.blockUI();
    if ($rootScope.webApp && !$rootScope.password) {
      var $iframe = $('#idpSessionIframe1');
      $iframe.attr("onLoad", qlIframeOnload());
      console.log("Iframe onload attr: " + $iframe.attr("onLoad"));
      $iframe.attr('src', qlSecuredListApplUrl);
      $iframe.css('position', 'absolute !important');
      $iframe.css('left', '-100px !important');
      $iframe.css('top', '-1000px !important');
    } else {
      showQLSSOApps();
    }
  }

  function formatAMPM(date) {
    var hours = date.getHours();
    var minutes = date.getMinutes();
    var ampm = hours >= 12 ? 'PM' : 'AM';
    hours = hours % 12;
    hours = hours ? hours : 12; // the hour '0' should be '12'
    minutes = minutes < 10 ? '0' + minutes : minutes;
    var strTime = hours + ':' + minutes + ' ' + ampm;
    return strTime;
  }


  $scope.openUrlInBrowser = function(urlToOpen, urlType) {
    if ($rootScope.webApp) {
      var iabExtLink = window.open(urlToOpen, "_blank");
    } else {
      $.blockUI();
      if (iabRef) {
        iabRef = window.open(urlToOpen, "_blank", "location=no,hidden=no,clearcache=no,enableViewportScale=yes");
        $.unblockUI();
      } else {
        iabRef = window.open(homepage, "_blank", "location=yes,hidden=yes,clearcache=yes,enableViewportScale=yes");
        var iabClose = function(data) {
          $.unblockUI();
          //window.location.href = "index.html#home";
        };
        iabRef.addEventListener("exit", iabClose);
        var hideBlockUi = function() {
          //iabRef.show();
          $.unblockUI();
          iabRef = window.open(urlToOpen, "_blank", "location=no,hidden=no,clearcache=no,enableViewportScale=yes");
        };
        var username = $rootScope.username;
        var password = $rootScope.password;
        var cd = "try { document.getElementById('userName').value = '" + username + "';document.getElementById('password').value = '" + password + "';} catch(e) {} document.getElementsByClassName('d2l-button')[0].click();";

        var cb = function(data) {};
        var loadStop = function(data) {
          iabRef.executeScript({
            code: cd
          }, cb);
          if (data.url.indexOf("login") == -1) {
            hideBlockUi();
            //iabRef.close();
            //iabRef = window.open(qlssourl, "_blank", "location=yes,hidden=no,clearcache=no,enableViewportScale=yes");
          }
        };
        iabRef.addEventListener("loadstop", loadStop);
        iabRef.addEventListener('loaderror', function(event) {
          $.unblockUI();
          window.open(homepage, "_blank", "location=yes,enableViewportScale=yes");
        });
      }
    }


  }



}

