  $scope.fileDownload = function(fileUrl) {
    $.blockUI();
    var fileTransfer = new FileTransfer();
    var uri = encodeURI(fileUrl);
    //var store = cordova.file.dataDirectory;
    var store = "";
    if (device.platform == "Android") {
      store = cordova.file.externalRootDirectory;
    } else {
      store = cordova.file.documentsDirectory;
    }
    var fileName = fileUrl.substr(fileUrl.lastIndexOf("/") + 1);
    try {
      fileTransfer.download(
        uri,
        store + fileName,
        function(entry) {
          if (device.platform == "Android") {
            window.open(entry.toURL(), '_system');
          } else {
            //window.open(entry.toURL(), '_blank', "location=no,enableViewportScale=yes");
            window.open(entry.toURL(), '_system', "location=no,enableViewportScale=yes");
            $.unblockUI();
          }
          return;
        },
        function(error) {
          $.unblockUI();
          alert("Download error: " + error.source);
        },
        false, {
          headers: {
            "Authorization": "Basic dGVzdHVzZXJuYW1lOnRlc3RwYXNzd29yZA=="
          }
        }
      );
    } catch (err) {
      $.unblockUI();
    }
  }

