  $scope.openUrlInBrowser = function(urlToOpen, urlType) {
    if ($rootScope.webApp) {
      var iabExtLink = cordova.InAppBrowser.open(urlToOpen, "_blank");
    } else {
      $.blockUI();
      if (iabRef) {
        iabRef = cordova.InAppBrowser.open(urlToOpen, "_blank", "location=no,hidden=no,clearcache=no,enableViewportScale=yes");
        $.unblockUI();
      } else {
        if (window.device.platform === "iOS") {
          iabRef = cordova.InAppBrowser.open(homepage, "_blank", "location=no,hidden=no,clearcache=yes,enableViewportScale=yes");
        } else {
          iabRef = cordova.InAppBrowser.open(homepage, "_blank", "location=no,hidden=yes,clearcache=yes,enableViewportScale=yes");
          iabRef.hide();
        }
        var iabClose = function(data) {
          $.unblockUI();
        };
        iabRef.addEventListener("exit", iabClose);
        var hideBlockUi = function() {
          iabRef.show();
          $.unblockUI();
          iabRef = cordova.InAppBrowser.open(urlToOpen, "_blank", "location=no,hidden=yes,clearcache=no,enableViewportScale=yes");
        };
        var username = $.jStorage.get("unifyedusername");
        if (username.indexOf("@") > -1) {
          username = username.substr(0, username.indexOf("@"));
        }
        var password = $.jStorage.get("unifyedpassword");

        //var cd = "try { document.getElementById('userName').value = '" + username + "';document.getElementById('password').value = '" + password + "';} catch(e) {} document.getElementsByClassName('d2l-button')[0].click();";
        var cd = "document.getElementById('userName').value = '" + username + "';document.getElementById('password').value = '" + password + "'; setTimeout(function(){ document.querySelector('.d2l-button').click(); },500); ";
        var cb = function(data) {};
        var loadStop = function(data) {
          iabRef.executeScript({
            code: cd
          }, cb);
          if (data.url.indexOf("login") == -1) {
            hideBlockUi();
            //iabRef.close();
            //iabRef = window.open(qlssourl, "_blank", "location=yes,hidden=no,clearcache=no,enableViewportScale=yes");
          }
        };
        iabRef.addEventListener("loadstop", loadStop);
        iabRef.addEventListener('loaderror', function(event) {
          $.unblockUI();
          cordova.InAppBrowser.open(homepage, "_blank", "location=yes,enableViewportScale=yes");
        });
      }
    }


  }

  $scope.updateNews = function(index, news) {
    var item = "news" + index;
    var eL = angular.element(document.getElementById(item));
    if (eL.children().length == 0) {
      eL.append(news.Body.Html);
      angular.forEach(eL.find('a'), function(value, iterator, obj) {
        var aEl = angular.element(value);
        var tempHref = aEl.attr("href");
        if ($rootScope.webApp) {
          if (tempHref && tempHref.lastIndexOf("/", 0) == 0) {
            aEl.attr("href", homepage + tempHref);
          }
        } else {
          aEl.bind('click', function(e) {
            //console.log("element");
            //console.log(e);
            var vhref = e.srcElement.href;
            if (vhref.indexOf("file://") == -1) {
              //Nothing
              e.preventDefault();
              e.stopPropagation();
              var iabExtLink = window.open(vhref, "_blank", "location=no,hidden=no,clearcache=no,enableViewportScale=yes");
            } else {
              e.preventDefault();
              e.stopPropagation();
              //create a D2L session and show the URL
              var linkUrl = homepage + vhref.replace("file://", "");
              $scope.openUrlInBrowser(linkUrl);
              return;
            }
          });
        }
      });
      angular.forEach(eL.find('img'), function(value, iterator, obj) {
        var imgEl = angular.element(value);
        var tempHref = imgEl.attr("src");
        imgEl.attr("src", homepage + tempHref);
      });
    }
    return "";
  }

}

