  $scope.refreshjQueryElements = function() {
    setTimeout(function() {
      $('.datepicker-group').each(function(i, obj) {
        var contentId = 'datetimepicker' + i;
        var dynContent = "<div><input type='text' class='form-control datetimepicker borderNone' id='" + contentId + "' data-index='" + i + "' /></div>"
          //dynContent = dynContent + "<div class='' ng-click='showDatepicker(" + i + ")'><div class='topPadding10px mini-column'> Select</div><div class='topPadding10px mini-column'> Duration</div></div>"        
        $(obj).append(dynContent);
      });

      $('.datetimepicker').datetimepicker({
        inline: false,
        sideBySide: true,
        format: 'HH:mm',
        useCurrent: false,
        icons: {
          left: "fa fa-arrow-left",
          right: "fa fa-arrow-right",
          up: "fa fa-arrow-up",
          down: "fa fa-arrow-down"
        }
      }).on('dp.change', function(e) {
        var dur = $(this).val();
        var durationArr = dur.split(':');
        var keyIndex = $(this).attr('data-index');
        var keyStr = $(this).parent().attr('data-key');
        console.log($(this).attr('data-key'));
        console.log($(this).parent());
        console.log("keystr=" + keyStr + " - keyIndex=" + keyIndex + " - dur=" + dur);

        var icounter = 0;
        angular.forEach($scope.attVerfModel.weeklyClasses, function(value1, key1) {
          angular.forEach(value1.nonscheduled_classes, function(value2, key2) {
            if (icounter == keyIndex /*&& value1.weekdatestr == keyStr*/ ) {
              value2.MINUTES_IN_TIME = dur;
              value2.MINUTES = (parseInt(durationArr[0]) * 60) + parseInt(durationArr[1]);
              console.log("value2.MINUTES=" + value2.MINUTES);
              icounter = 9999;
            }
            icounter++;
          });
        });
      });
      var acounter = 0;
      angular.forEach($scope.attVerfModel.weeklyClasses, function(value1, key1) {
        angular.forEach(value1.nonscheduled_classes, function(value2, key2) {
          $('#datetimepicker' + acounter).val(value2.MINUTES_IN_TIME);
          acounter++;
        });
      });

      try {
        $scope.$apply();
      } catch (ex) {
        //console.log(ex);
      }
    }, 300);

  }

  $scope.attVerfLoadClasses = function(pDate) {
    var postdata = "p_start_date=" + pDate;

    $rootScope.postAPI(config, facultyClassScheduleEndpoint, postdata, function(data) {
      if (data.p_out[0] && data.p_out[0].summary) {
        if (typeof data.p_out[0].summary == "string") {
          var result = JSON.parse(data.p_out[0].summary);
          data.p_out[0].summary = result;
        }
        $scope.attVerfModel.facultyschedule = data.p_out[0].summary[0];
        console.log("$scope.attVerfModel.facultyschedule");
        console.log($scope.attVerfModel.facultyschedule);
        console.log("pDate=" + pDate);
        if (!$scope.attVerfModel.facultyschedule.header_info.curr_week_start_date) {
          $scope.attVerfModel.nonSchClassesCount = 0;
          $scope.attVerfModel.schClassesCount = 0;
          return;
        }
        var currDate;
        if (!pDate) {          
          currDate = new Date($scope.attVerfModel.facultyschedule.header_info.curr_week_start_date);
          $scope.prepareWeeks($scope.attVerfModel.facultyschedule.header_info.curr_week_start_date);
          $scope.attVerfModel.selweek = $scope.attVerfModel.facultyschedule.header_info.curr_week_start_date
          $scope.attVerfModel.selweekDate = $scope.attVerfModelGetWeekDesc($scope.attVerfModel.facultyschedule.header_info.curr_week_start_date);
        } else {
          currDate = new Date(pDate);
        }
        if (config.middlewareServerUrl == "https://kryptosda.kryptosmobile.com/kryptosds") {
          $scope.attVerfModel.facultyschedule.nonscheduled_classes = [{
            "TERM_CODE": "201706",
            "CRN": "68026",
            "SUBJ_CODE": "CIS",
            "TITLE": "",
            "CRSE_NUMB": "109",
            "SEQ_NUMB": "WEB",
            "PTRM_CODE": "1",
            "SCHD_DESC": "Lecture",
            "SURROGATE_ID": "108074"
          }];
        }

        $scope.attVerfModel.nonSchClassesCount = $scope.attVerfModel.facultyschedule.nonscheduled_classes.length;
        var classCount1 = 0;

        angular.forEach($scope.attVerfModel.facultyschedule.class_list, function(value, key) {
          if (value.MON_DAY) {
            classCount1++;
          }
          if (value.TUE_DAY) {
            classCount1++;
          }
          if (value.WED_DAY) {
            classCount1++;
          }
          if (value.THU_DAY) {
            classCount1++;
          }
          if (value.FRI_DAY) {
            classCount1++;
          }
          if (value.SAT_DAY) {
            classCount1++;
          }
          if (value.SUN_DAY) {
            classCount1++;
          }
        });
        $scope.attVerfModel.schClassesCount = classCount1;
        angular.forEach($scope.attVerfModel.facultyschedule.nonscheduled_classes, function(value, key) {
          value.MINUTES = 0;
          value.MINUTES_IN_TIME = "";
          value.displayMinutes = false;
        });
        $scope.attVerfModel.weeklyClasses = [];
        var i = 0;
        var processDate = new Date(currDate.getFullYear(), currDate.getMonth(), currDate.getDate());
        var xcounter = 0;
        for (i = 0; i <= 6; i++) {
          var strWeekStr = "";
          switch (processDate.getDay()) {
            case 0:
              strWeekStr = weekDaysShort[0];
              break;
            case 1:
              strWeekStr = weekDaysShort[1];
              break;
            case 2:
              strWeekStr = weekDaysShort[2];
              break;
            case 3:
              strWeekStr = weekDaysShort[3];
              break;
            case 4:
              strWeekStr = weekDaysShort[4];
              break;
            case 5:
              strWeekStr = weekDaysShort[5];
              break;
            case 6:
              strWeekStr = weekDaysShort[6];
              break;
          }
          var monthShortStr = uMonths[processDate.getMonth()];
          var dayShortStr = processDate.getDate();
          var classSch = {
            "weekdate": processDate,
            "weekdatestr": strWeekStr + ", " + monthShortStr + " " + dayShortStr,
            "classes": [],
            "nonscheduled_classes": []
          };
          var sclasslist = JSON.parse(JSON.stringify($scope.attVerfModel.facultyschedule.class_list));
          angular.forEach(sclasslist, function(value, key) {
            value.uniquekey = xcounter;
            xcounter++;
            if (processDate.getDay() == 1 && value.MON_DAY) {
              classSch.classes.push(value);
            } else if (processDate.getDay() == 2 && value.TUE_DAY) {
              classSch.classes.push(value);
            } else if (processDate.getDay() == 3 && value.WED_DAY) {
              classSch.classes.push(value);
            } else if (processDate.getDay() == 4 && value.THU_DAY) {
              classSch.classes.push(value);
            } else if (processDate.getDay() == 5 && value.FRI_DAY) {
              classSch.classes.push(value);
            } else if (processDate.getDay() == 6 && value.SAT_DAY) {
              classSch.classes.push(value);
            } else if (processDate.getDay() == 0 && value.SUN_DAY) {
              classSch.classes.push(value);
            }
          });
          var nclasslist = JSON.parse(JSON.stringify($scope.attVerfModel.facultyschedule.nonscheduled_classes));
          angular.forEach(nclasslist, function(value, key) {
            if (!value.MINUTES) value.MINUTES = 0;
            var vhours = parseInt(value.MINUTES / 60).toString();
            var hoursStr = (vhours.length == 1) ? "0" + vhours : vhours;
            var vmins = (value.MINUTES % 60).toString();
            var minsStr = (vmins.length == 1) ? "0" + vmins : vmins;
            value.MINUTES_IN_TIME = hoursStr + ":" + minsStr;
            value.uniquekey = xcounter;
            xcounter++;
            classSch.nonscheduled_classes.push(value);
          });
          $scope.attVerfModel.weeklyClasses.push(classSch);
          processDate = new Date(processDate.getFullYear(), processDate.getMonth(), processDate.getDate() + 1);
        }
      }

      $scope.refreshjQueryElements();
    });
  }

