    }
  }

  function initApplet() {
    var tab = "";
    $scope.attVerfModel.page = 1;
    $scope.attVerfModel.selTab = 1;
    tab = ($scope.attVerfModel.selTab == 1) ? "tab1" : tab;
    tab = ($scope.attVerfModel.selTab == 2) ? "tab2" : tab;
    $("#tab1").removeClass("toolbar-column-active");
    $("#tab2").removeClass("toolbar-column-active");
    $("#" + tab).addClass("toolbar-column-active");
    $scope.attVerfLoadClasses('');
  }

  // Initialize applet
  initApplet();

  /*
      Detail pages functions
  */
  $scope.attVerfGotoHomePage = function() {
    $scope.attVerfModel.page = 1;
  }

  $scope.updateAttendance = function() {
    var pidms = "";
    var att_inds = "";
    var rowcount = 0;
    angular.forEach($scope.attVerfModel.classlist, function(value, key) {
      value.ATTENDED = (value.marked) ? 'Y' : 'N';
      if (rowcount > 0) {
        pidms = pidms + ",";
        att_inds = att_inds + ",";
      }
      pidms = pidms + value.STUD_PIDM;
      att_inds = att_inds + value.ATTENDED;
      rowcount += 1;
    });
    var attDate = $scope.attVerfModel.selFormattedDate;

    var postdata = "stud_pidm=" + pidms + "&attend_ind=" + att_inds + "&attend_date=" + attDate + "&meet_id=" + $scope.attVerfModel.myclass.SURROGATE_ID + "&minutes=" + $scope.attVerfModel.myclass.MINUTES;

    $rootScope.postAPI(config, facultyUpdateAttEndpoint, postdata, function(data) {
      showAlert("Attendance data has been updated successfully");
    });
  }

  $scope.deleteAttendanceData = function() {
    var mpostdata = "meet_id=" + $scope.attVerfModel.myclass.SURROGATE_ID + "&meet_date=" + $scope.attVerfModel.selFormattedDate;
    $rootScope.postAPI(config, facultyAttDeleteEndpoint, mpostdata, function(mdata) {
      var mess = mdata.p_out[0].summary;
      showAlert(mess);
      $scope.attVerfModel.page = 1;
    });
  }

  $scope.deleteConfirm = function() {
    var onConfirm = function(buttonIndex) {
      if (buttonIndex == 1) {
        $scope.deleteAttendanceData();
      }
    };
    if (window.device) {
      navigator.notification.confirm(
        'Are you sure you want to delete attendance data for this date?', // message
        onConfirm, // callback to invoke with index of button pressed
        'Delete Attendance Data', // title
        ['Yes', 'No'] // buttonLabels
      );
    } else {
      $.confirm({
        title: 'Delete Attendance Dat',
        content: 'Are you sure you want to delete attendance data for this date?',
        buttons: {
          confirm: function() {
            $scope.deleteAttendanceData();
          },
          cancel: function() {
            //
          }
        }
      });
    }

  }

  $scope.attCheckboxClicked = function(event, studinfo) {
    $scope.attVerfModel.presentCount = 0
    $scope.attVerfModel.absentCount = 0;
    studinfo.modified = true;
    angular.forEach($scope.attVerfModel.classlist, function(value, key) {
      if (value.marked) {
        $scope.attVerfModel.presentCount++;
      } else {
        $scope.attVerfModel.absentCount++;
      }
    });
  }

  $scope.markAllPresent = function() {
    $scope.attVerfModel.presentCount = 0;
    $scope.attVerfModel.absentCount = 0;
    angular.forEach($scope.attVerfModel.classlist, function(value, key) {
      value.marked = true;
      $scope.attVerfModel.presentCount++;
    });
  }

  $scope.markAllAbsent = function() {
    $scope.attVerfModel.presentCount = 0;
    $scope.attVerfModel.absentCount = 0;
    angular.forEach($scope.attVerfModel.classlist, function(value, key) {
      value.marked = (value.ATTENDED == 'Y') ? true : false;
      if (value.marked) {
        $scope.attVerfModel.presentCount++;
      } else {
        $scope.attVerfModel.absentCount++;
      }
    });
  }

  function attVerfSetDisplayFieldsForClassRoster() {
    angular.forEach($scope.attVerfModel.attendancereport, function(value, key) {
      value.shortName = value.FIRST_NAME.substr(0, 1) + value.LAST_NAME.substr(0, 1);
    });
  }

}

function pageprocessorMarkAttendance35(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  /******************************************************************************************************************************************/
  var config = {
    "middlewareServerUrl": pagedef.middlewareServerUrl,
    "product": pagedef.product
  }
  config.tokenType = "oauth";
  /* var geoLocationsExample = [{
                                 "BLDG_CODE" : "MAIN",
                                 "LATITUDE" : "42.593287600",
                                 "LONGITUDE": "-71.983985500"
                               }]*/
  /* Assign configuration params values to local variables */
  var middlewareServerUrl = pagedef.middlewareServerUrl;
  var licenseKey = pagedef.licenseKey;
  var geoLocations = pagedef.geoLocations;
  var periodFrom = pagedef.periodFrom;
  var periodTo = pagedef.periodTo;
  var geoRange = pagedef.geoRange;
  var disableMobileVerification = pagedef.disableMobileVerification;
  var disableWebLocationVerification = pagedef.disableWebLocationVerification;
  var attRequiredPercentage = pagedef.attRequiredPercentage;
  /* Declare page level variables  */
  var myScheduleEndpoint = "/bannermyschedulefortoday";
  var myAttInfoEndpoint = "/bannerstudattendanceinfo";
  var markMyAttEndpoint = "/bannermarkstudattendance";
  var geoOptions = {
    enableHighAccuracy: true,
    timeout: 5000,
    maximumAge: 0
  };

  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, "Mark Attendance", null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: 'Mark Attendance',
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      } else {
        alert(msg);
      }

    }
  }

  if (!pagedef.middlewareServerUrl) {
    showAlert("You must enter the proper middleware url!");
    return;
  }

  if (!pagedef.product) {
    showAlert("You must setup the product name parameter!");
    return;
  }

  try {
    geoLocations = JSON.parse(geoLocations);
  } catch (err) {
    showAlert("You must setup the Geo locations correctly for all the campus buildings before proceeding !");
    return;
  }

  /* Page level validations */
  if (window.device && disableMobileVerification == "on" && !geoLocations) {
    showAlert("You must setup the Geo locations for all the campus buildings before proceeding !");
    return;
  }

  if (!window.device && disableWebLocationVerification == "on" && !geoLocations) {
    showAlert("You must setup the Geo locations for all the campus buildings before proceeding !");
    return;
  }

  if (!geoRange || isNaN(geoRange.trim())) {
    showAlert("You must setup the geo range configuration parameter correctly !");
  }

  if (!attRequiredPercentage || isNaN(attRequiredPercentage.trim())) {
    showAlert("You must enter the required attendance percentage correctly !");
  }

  $scope.verifycurrentlocation = function(schedule) {
    $scope.locationVerified = false;
    var skipLocationVerification = false;
    // if configured to skip location verification, then bypass verification
    if (window.device) {
      skipLocationVerification = (disableMobileVerification == "on") ? true : false;
    } else {
      skipLocationVerification = (disableWebLocationVerification == "on") ? true : false;
    }
    if (skipLocationVerification) {
      $scope.locationVerified = true;
      $scope.markClassAttendance(schedule);
      return;
    }

    function success(pos) {
      var crd = pos.coords;

      console.log('Your current position is: (' + crd.latitude + "," + crd.longitude + ") with accuracy: " + crd.accuracy + " meters.");
      var data = {
        "flat": crd.latitude,
        "flng": crd.longitude,
        "tlat": schedule.latitude,
        "tlng": schedule.longitude
      }
      console.log(data);
      // check if you are in the campus building accepted range to mark attendance
      $http({
        method: "POST",
        url: "https://kryptosda.kryptosmobile.com/kryptosds/geo/distance",
        data: data
      }).
      success(function(data, status, headers, config) {
        $.unblockUI();
        console.log("geo location verification distance: " + data.distance);
        //data.distance = ($rootScope.middlewareServerUrl == "https://kryptosda.kryptosmobile.com/kryptosds") ? 1 : data.distance;
        if (data.distance) {
          if (data.distance <= geoRange) {
            $scope.locationVerified = true;
          } else {
            $scope.locationVerified = false;
          }
        } else {
          $scope.locationVerified = false;
        }
        $scope.markClassAttendance(schedule);
      }).
      error(function(data, status, headers, config) {
        $.unblockUI();
        $scope.locationVerified = false;
        $scope.markClassAttendance(schedule);
      });
    };

    function error(err) {
      $scope.locationVerified = false;
      var msg = "";
      switch (error.code) {
        case error.PERMISSION_DENIED:
          msg = "User denied the request for Geolocation."
          break;
        case error.POSITION_UNAVAILABLE:
          msg = "Location information is unavailable."
          break;
        case error.TIMEOUT:
          msg = "The request to get user location timed out."
          break;
        case error.UNKNOWN_ERROR:
          msgL = "An unknown error occurred."
          break;
      }
      console.warn('ERROR(' + err.code + '): ' + msg);
      showAlert(msg);
      retVal = false;
    };
    // Get the current geo location
    navigator.geolocation.getCurrentPosition(success, error, geoOptions);
  }

  $scope.findBuildingLocation = function(buildingCode) {
    var loc = {};
    if (geoLocations && geoLocations.length > 0) {
      angular.forEach(geoLocations, function(value, key) {
        if (value.BLDG_CODE == buildingCode) {
          loc = {
            "LATITUDE": value.LATITUDE,
            "LONGITUDE": value.LONGITUDE
          }
          return true;
        }

      });
    }
    return loc;
  }


  $scope.markClassAttendance = function(schedule) {
    if (!$scope.locationVerified) {
      showAlert("You are not verified to be in the class premises !");
      return;
    }
    $scope.markattendance = false;
    $scope.message = "";
    var postdata = "stud_pidm=" + schedule.PIDM + "&meet_id=" + schedule.MEET_ID + "&minutes=" + schedule.MINUTES;
    console.log(postdata);
    $rootScope.postAPI(config, markMyAttEndpoint, postdata, function(data) {
      console.log(data);
      if (data) {
        $scope.attresults = data.p_out[0];
        console.log(data.p_out[0]);
        if ($scope.attresults.summary && $scope.attresults.summary[0].success) {
          $scope.message = $scope.attresults.summary[0].success;
          //Attendance marked successfully 
          $scope.btnMsg = "MARKED";
          $scope.btnDisabled = true;
          $scope.marked = true;
          $('#myclass').removeClass('shadow');
          $('#btnMark').css('btn-custom-disabled');
        } else if ($scope.attresults.summary && $scope.attresults.summary[0].error) {
          $scope.message = $scope.attresults.summary[0].error;
        } else {
          $scope.message = "System error. Please contact administrator.";
        }
      } else {
        $scope.message = "System error. Please contact administrator.";
      }
      $scope.markattendance = false;
    });
  }

  $scope.selectTab = function(tab) {
    $("#tab1").removeClass("toolbar-column-active");
    $("#tab2").removeClass("toolbar-column-active");
    $("#" + tab).addClass("toolbar-column-active");
    if (tab == "tab1") {
      $scope.selTab = 1
    } else if (tab == "tab2") {
      $scope.selTab = 2
    }
  }

  function loadMySchedule() {
    /* Get the student schedule for the day with config params */
    $rootScope.getAPI(config, myScheduleEndpoint, function(data) {
      $scope.myschedule = data.myschedulefortoday;
      $scope.currentdate = data.currentdatetime[0].CURRENTDATE;
      console.log($scope.myschedule);
      console.log($scope.currentdate);
      if ($scope.myschedule.length > 0) {
        $scope.markattendance = true;
        angular.forEach($scope.myschedule, function(value, key) {
          var loc = $scope.findBuildingLocation(value.BLDG_CODE);
          value.latitude = loc.LATITUDE;
          value.longitude = loc.LONGITUDE;
          $scope.expectedHours = 0;
          $rootScope.getAPI(config, myAttInfoEndpoint, function(data) {
            console.log("att info:");
            console.log(data);
            // check if the class has defined meeting times or not
            if (!value.MINUTES) {
              console.log("minutes=" + value.MINUTES);
              console.log("def hrs=" + data.defaultexpectedhours.length);
              if (data && data.defaultexpectedhours && data.defaultexpectedhours.length > 0) {
                console.log("exp hrs > 0");
                //var defExpectedHrs = data.defaultexpectedhours[0].EXPECTED_HOURS;
                //$scope.expectedHours = defExpectedHrs;
              } else {
                console.log("exp hours = 0");
                $scope.markattendance = false;
                $scope.message = "You cannot enter attendance until your faculty enters Expected hours for the class meeting.";
                return;
              }
            }

            if (data && data.studattendanceinfo && data.studattendanceinfo.length > 0) {
              console.log("you are here");
              var attend_ind = data.studattendanceinfo[0].ATTEND_IND;
              if (attend_ind && attend_ind == 'Y') {
                $scope.markattendance = false;
                $scope.message = "You have already marked attendance for the class.";
                return;
              }
            }
          });
          return;
        });

      } else {
        $scope.markattendance = false;
        $scope.message = "You have no classes now to mark attendance.";
      }
    });
  }

  function loadAttendanceReport() {
    $rootScope.getAPI(config, "/bannerstudentattendancereport", function(report) {
      $scope.attendancereport = report.studentattendancereport;
      if($scope.attendancereport.length > 0){
        $scope.showReport = true;
      }else{
        $scope.showReport = false;
      }
      console.log(report);
    });
  }

  function initApplet() {
    $scope.locationVerified = false;
    $scope.markattendance = false;
    $scope.attendancereport = [];
    $scope.selTab = ($rootScope.selTab && $rootScope.selTab > 0) ? $rootScope.selTab : 1;
    $scope.attPercentage = attRequiredPercentage;
    console.log("$scope.attPercentage=" + $scope.attPercentage);
    var tab = "";
    tab = ($scope.selTab == 1) ? "tab1" : tab;
    tab = ($scope.selTab == 2) ? "tab2" : tab;
    $("#tab1").removeClass("toolbar-column-active");
    $("#tab2").removeClass("toolbar-column-active");
    $("#" + tab).addClass("toolbar-column-active");
    $scope.btnMsg = "MARK ATTENDANCE";
    $scope.btnDisabled = false;
    $scope.marked = false;
    loadMySchedule();
    loadAttendanceReport();
    /*try {
      $scope.$apply();
    } catch (ex) {
      //
    }*/
  }

  // Initialize applet
  initApplet();

}

