    $rootScope.getAPI(config, url, function(data) {
        if (typeof data.p_out[0].summary == "string") {
            data.p_out[0].summary = JSON.parse(data.p_out[0].summary)
        }
      $scope.sections = data.p_out[0].summary[0].sections;
      $.unblockUI();
      $scope.showMidGradeTab = false;
      $scope.showFinGradeTab = false;
      angular.forEach($scope.sections, function(value, key) {
        if (value.status_ind == 'B' || value.status_ind == 'M') {
            $scope.showMidGradeTab = true;
        } 
        if (value.status_ind == 'B' || value.status_ind == 'F') {
            $scope.showFinGradeTab = true;
        }         
        value.isrolled = false;
        value.isinprogress = false;
        value.toggle = true;
        value.ftoggle = true;
        value.toggleclass = "fa fa-angle-down";
        value.Ftoggleclass = "fa fa-angle-down";
        value.courseDetails = "";
        value.FcourseDetails = "";
        if (value.rolled_status == 'N') {
          value.isrolled = false;
          value.isinprogress = false;
        } else if (value.rolled_status == 'C') {
          value.isrolled = true;
          value.isinprogress = false;
        } else if (value.rolled_status == 'IP') {
          value.isrolled = false;
          value.isinprogress = true;
        }
        value.gradeClass = "";
        if (value.graded_status) {
          if (value.graded_status == 'C') {
            value.gradeClass = "fa fa-circle circleGreen";
          } else if (value.graded_status == 'IP') {
            value.gradeClass = "fa fa-circle circleRed";
          } else if (value.graded_status == 'N') {
            value.gradeClass = "fa fa-circle circleGrey";
          }
        }
        value.midgradeClass = "";
        if (value.mid_grd_status) {
          console.log("value.mid_grd_status", value.mid_grd_status)
          if (value.mid_grd_status == 'C') {
            value.midgradeClass = "fa fa-circle circleGreen";
            console.log("value.mid_grd_status", value.mid_grd_status)
          } else if (value.mid_grd_status == 'IP') {
            value.midgradeClass = "fa fa-circle circleRed";
          } else if (value.mid_grd_status == 'N') {
            value.midgradeClass = "fa fa-circle circleGrey";
          }
        }
