      }
      $scope.midroster = data.p_out[0].summary[0].classlist;
      $rootScope.termdropdown = $scope.termdropdown;
      $rootScope.selectedvalue = $scope.selectedvalue;
      $rootScope.pageNum = "2";
      $scope.recordsDirty = false;
      $scope.selectTab('tab3');
      angular.forEach($scope.midroster, function(value, key) {
        value.dirty = false;
        value.alertClass = "";
      });

    });
  };

  $scope.finalgradeentry = function(section) {
    $scope.section = section;
    var url = "/bannerfacultyfinalgraderoster/termcode=" + section.term + "&crn=" + section.CRN;
    $rootScope.getAPI(config, url, function(data) {     
        if (data && data.p_out[0] && typeof data.p_out[0].summary == "string") {
            data.p_out[0].summary = JSON.parse(data.p_out[0].summary);
        }
      $rootScope.finalroster = data.p_out[0].summary[0].classlist;
      $rootScope.termdropdown = $scope.termdropdown;
      $rootScope.selectedvalue = $scope.selectedvalue;
      $rootScope.pageNum = "3";
      $scope.selectTab('tab4');
      $scope.enableUpdate = false;
      $scope.recordsDirty = false;
      $scope.incompletelistpage = false;
      angular.forEach($scope.finalroster, function(value, key) {
        value.dirty = false;
        value.alertClass = "";
        value.gradeMessage = "";
        if (value.rolled_ind == 'Y') {
          value.isdisabled = true;
          value.gradeMessage = value.grade;
        } else {
          value.isdisabled = false;
        }
    
        if (value.gradable_ind == 'N') {
          value.gradeMessage = "Not Gradable";
        } else if (value.rolled_ind == 'N') {
          if (value.update_ind == '3') {
            value.isdisabled = false;
          } else if (value.update_ind == '2') {
            value.isdisabled = true;
          }
        } else {
          value.isdisabled = true;
        }
