    $scope.updateFinalGrades = function() {
        var rowids = "";
        var grades = "";
        var attendDates = "";
        var attendHours = "";
        var update_ind = "";
        var grade_upd_ind = "";
        var i = 0;
        $scope.results = [];
    
        angular.forEach($scope.finalroster, function(value, key) {
          if (value.dirty) {
            if (i > 0) {
              rowids = rowids + ",";
              grades = grades + ",";
              attendDates = attendDates + ",";
              attendHours = attendHours + ",";
            }
            rowids = rowids + value.row_id;
            grades = grades + value.grade;
            if (value.last_attend_date) {
              console.log(value.last_attend_date);
              var mdate = new Date(value.last_attend_date + " 00:00:00");
    
              attendDates = attendDates + ("0" + (mdate.getDate())).slice(-2) + "-" + months[mdate.getMonth()] + "-" + mdate.getFullYear();
            } else {
              attendDates = attendDates + "";
            }
            //attendDates = attendDates + value.last_attend_date;
            attendHours = attendHours + value.last_attend_hours;
            i = i + 1;
          }
          update_ind = value.update_ind;
          grade_upd_ind = value.gradable_ind;
          console.log("attendDates:" + attendDates);
        });
    
        //save the data
        if (rowids) {
          var searchURL = "/bannerfacultyfinalgradeupdate";
          var postdata = "termcode=" + $scope.section.term + "&crn=" + $scope.section.CRN +
            "&ptrm_in=" + $scope.section.ptrm +
            "&update_ind=" + update_ind +
            "&grade_upd_ind=" + grade_upd_ind +
            "&rowids=" + rowids +
            "&grades=" + grades +
            "&attend=" + attendDates +
            "&hours=" + attendHours;
    
          var url = searchURL;
    
          $rootScope.postAPI(config, url, postdata, function(data) {
            $scope.incompletelistpage = false;
            if (data.p_out[0].summary[0].error) {
              $scope.errors = data.p_out[0].summary[0].error;
              angular.forEach($scope.errors, function(value, key) {
                var cssClass = "alert alert-warning";
                $scope.showUpdateResults(value.row_id, cssClass, value.message);
              });
              return;
            } else if (data.p_out[0].summary[0].info && data.p_out[0].summary[0].info.length > 0) {
              angular.forEach($scope.finalroster, function(value, key) {
                if (value.dirty) {
                  value.alertClass = "alert alert-success";
                  value.message = data.p_out[0].summary[0].info[0];
                }
              });
            } else if (data.p_out[0].summary[0].warning && data.p_out[0].summary[0].warning.length > 0) {
              angular.forEach($scope.finalroster, function(value, key) {
                if (value.dirty) {
                  value.alertClass = "alert alert-info";
                  value.message = data.p_out[0].summary[0].info[0];
                }
              });
            } else if (data.p_out[0].summary[0].incompletelist) {
              if (data.p_out[0].summary[0].incompletelist.length > 0) {
                $scope.incompletelist = data.p_out[0].summary[0].incompletelist;
                angular.forEach($scope.incompletelist, function(value, key) {
                  value.dirty = false;
                  value.alertClass = "";
                  value.gradeMessage = "";
                  if (value.rolled_ind == 'Y') {
                    value.isdisabled = true;
                    value.gradeMessage = value.grade;
                  } else {
                    value.isdisabled = false;
                  }
    
                  if (value.gradable_ind == 'N') {
                    value.gradeMessage = "Not Gradable";
                  } else if (value.rolled_ind == 'N') {
                    if (value.update_ind == '3') {
                      value.isdisabled = false;
                    } else if (value.update_ind == '2') {
                      value.isdisabled = true;
                    }
                  } else {
                    value.isdisabled = true;
                  }
                  if (!value.isdisabled) {
                    $scope.enableUpdate = true;
                  }
                });
                $scope.incompletelistpage = true;
              } else {
                //
              }
            }
            // reset the information
            console.log("clearing dirty tags");
            angular.forEach($scope.finalroster, function(value, key) {
              value.dirty = false;
            });
            $scope.recordsDirty = false;
          });
        }
    }
    
    $scope.updateIGrades = function() {
