          $rootScope.postAPI(config, url, postdata, function(data) {
            if (data.p_out[0].summary[0].info && data.p_out[0].summary[0].info.length > 0) {
              var vmes = data.p_out[0].summary[0].info[0];
              if (vmes == "COMMIT_SUCCESS") {
                $scope.ialertClass = "alert alert-success";
                $scope.imessage = "The changes were updated successfully.";
                $scope.incompletelistpage = false;
              } else if (vmes == "BOTH_VALUES") {
                $scope.ialertClass = "alert alert-warning";
                $scope.imessage = "You haven't entered values to any of the fields.";
              } else if (vmes == "DATE_FORMAT") {
                $scope.ialertClass = "alert alert-warning";
                $scope.imessage = "Invalid date format.";
              } else if (vmes == "INVALID_DATE") {
                $scope.ialertClass = "alert alert-warning";
                $scope.imessage = "Invalid date entered.";
              } else {
                $scope.ialertClass = "";
                $scope.imessage = "";
              }
            }
          });
        }
    }

    $scope.showUpdateResults = function(rowid, cssClass, message) {
        angular.forEach($scope.finalroster, function(value, key) {
          if (value.row_id == rowid) {
            value.alertClass = cssClass;
            value.message = message;
            console.log(value);
          }
        });
    }

