function pageprocessorPROFILEBANNER(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  var config = {
    "middlewareServerUrl": pagedef.middlewareServerUrl,
    "product": "banner"
  }
  config.tokenType = "oauth";
  $rootScope.demoMode = false;
  $rootScope.middlewareServerUrl = pagedef.middlewareServerUrl;

  $scope.Personal = false;
  $scope.Academic = false;
  $scope.listdata = false;

  /* Assign configuration params values to local variables */
  var middlewareUrl = pagedef.middlewareServerUrl;
  var licenseKey = pagedef.licenseKey;
  var product = pagedef.product;

  /* Declare page level variables  */
  var profileBannerEndpoint = "/banneruserprofile";
  $scope.title = "Profile Banner"


  /*function to show alert message*/
  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, $scope.title, null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: $scope.title,
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      } else {
        alert(msg);
      }
    }
  }


  $scope.changeStatus = function(pageStatus) {
    switch (pageStatus) {

      case "personal":
        $scope.Personal = true;
        $scope.Academic = false;
        $("#tab1").addClass("toolbar-column-active")
        $("#tab2").removeClass("toolbar-column-active")
        break;
      case "academic":
        $scope.Academic = true;
        $scope.Personal = false;
        $("#tab1").removeClass("toolbar-column-active")
        $("#tab2").addClass("toolbar-column-active")
        break
    }

  }
  $scope.userData = null
    /*$.blockUI();*/
  $rootScope.getAPI(config, profileBannerEndpoint, function(data) {
    if (data) {
      $scope.userData = data
      console.log($scope.userData.p_out[0].summary);
      if (typeof $scope.userData.p_out[0].summary == "string") {
        $scope.userData.p_out[0].summary = JSON.parse($scope.userData.p_out[0].summary);
      }
      $scope.changeStatus("personal");
    } else {

      showAlert("No information is available for your account.")
    }
  });

  $scope.formatDate = function(dt) {
    if (!dt) return '';
    var t1 = dt.substring(0, 2);
    var t2 = dt.substring(3, 6).toLowerCase();
    var t3 = dt.substring(7, 11);
    var month = t2.substring(0, 1).toUpperCase() + t2.substring(1);
    return month + " " + t1 + ", " + t3;
  }

}

