      };
      window.plugins.actionsheet.show(options, ActionSheetSuccess);
    };



    var weekName = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    var monthName = ['January', 'February', 'March', 'April', 'May', 'June', 'July',
      'August', 'September', 'October', 'November', 'December'
    ];


    var leapYear = function(year) {
      return ((year % 4 == 0) && (year % 100 != 0)) || (year % 400 == 0);
    }

    // Format date value
    var formatDateValues = function(date) {
      var dateReturn = new Date(date);
      dateReturn = Math.floor(dateReturn.getTime());
      return dateReturn;
      //  return date.getDate() + "-" + (date.getMonth() + 1) + "-" + date.getFullYear();
    }

    // Recurrent date calculator functions
    var calculateRecurringDates = {
      'everyDay': function(startDate, endDate) {
        var dateToReturn = [];
        console.log("everday");
        while (startDate <= endDate) {
          console.log("everday1");
          dateToReturn.push(formatDateValues(startDate));
          startDate.setDate(startDate.getDate() + 1);
        }
        console.log("dateToReturn");
        return dateToReturn;
      },
      'everyWeekday': function(startDate, endDate) {
        var dateToReturn = [];
        while (startDate <= endDate) {
          dateToReturn.push(formatDateValues(startDate));
          startDate.setDate(startDate.getDate() + 7);
        }
        return dateToReturn;
      },
      'everyWorkday': function(startDate, endDate) {
        var dateToReturn = [];
        while (startDate <= endDate) {
          if (([1, 2, 3, 4, 5]).indexOf(startDate.getDay()) != -1) {
            dateToReturn.push(formatDateValues(startDate));
          }
          startDate.setDate(startDate.getDate() + 1)
        }
        return dateToReturn;
      },
      'dayOfMonth': function(startDate, endDate) {
        var dateToReturn = [formatDateValues(startDate)];

        var startDateMonth = startDate.getMonth();
        var startDateDay = startDate.getDate();

        while (startDate <= endDate) {
          if (startDateMonth != startDate.getMonth()) {
            if (startDateDay == startDate.getDate()) {
              startDateMonth = startDate.getMonth();
              dateToReturn.push(formatDateValues(startDate));
            }
          }
          startDate.setDate(startDate.getDate() + 1)
        }
        return dateToReturn;
      },
      'firstWeekday': function(startDate, endDate) {
        var dateToReturn = [formatDateValues(startDate)];
        var startDateMonth = startDate.getMonth();
        while (startDate <= endDate) {
          if (startDateMonth != startDate.getMonth()) {
            startDateMonth = startDate.getMonth();
            dateToReturn.push(formatDateValues(startDate));
          }

          startDate.setDate(startDate.getDate() + 7)
        }
        return dateToReturn;
      },
      'everyMonthDay': function(startDate, endDate) {
        var dateToReturn = [formatDateValues(startDate)];

        var startMonthDay = startDate.getDay();
        var numberOfDays = 365;
        if (leapYear(startDate.getFullYear())) {
          numberOfDays = 366;
        }

        while (startDate <= endDate) {
          if (startMonthDay != startDate.getDay()) {
            dateToReturn.push(formatDateValues(startDate));
          }

          startDate.setDate(startDate.getDate() + numberOfDays);
        }
        return dateToReturn;
      }
    };

    var repeatEventOptionsDefault = [{
      'value': 'never',
      'text': 'Never'
    }, {
      'value': 'everyDay',
      'text': 'Every Day'
    }, {
      'value': 'everyWeekday',
      'text': 'Every #WEEK#'
    }, {
      'value': 'everyWorkday',
      'text': 'Every Workday'
    }, {
      'value': 'dayOfMonth',
      'text': 'Day #DAY# of every month'

    }, {
      'value': 'firstWeekday',
      'text': 'Every first #WEEK#'

    }, {
      'value': 'everyMonthDay',
      'text': 'Every #MONTH# #DAY#'
    }];

    // Replace Month, Day, Week event
    $scope.replaceKeywords = function(value) {
      return value.replace("#MONTH#", $scope.selectedMonth)
        .replace("#DAY#", $scope.selectedDay)
        .replace("#WEEK#", $scope.selectedWeek);
    }

