    $scope.handleEventChange = function() {
      try {
        $scope.repeatEventOptions = [];
        var repeatEventOptionsDefaultCopy = [];
        angular.copy(repeatEventOptionsDefault, repeatEventOptionsDefaultCopy);
        console.log(repeatEventOptionsDefaultCopy);
        angular.forEach(repeatEventOptionsDefaultCopy, function(eventType, eventIndex) {
          console.log(eventType)
          eventType.value = $scope.replaceKeywords(eventType.value);
          eventType.text = $scope.replaceKeywords(eventType.text);
          $scope.repeatEventOptions.push(eventType);
        });
        // $scope.$apply();
        $scope.$applyAsync();
        //  console.log("newonetest", $scope.repeatEventOptions);
      } catch (e) {
        console.log(e)
      }

    }

    // Fetch day, month, year, week for selected month
    $scope.fetchDayMonthYear = function() {
      if ($scope.event.startDate) {
        $scope.selectedDate = new Date($scope.event.startDate);
      }
      var selectedDate = $scope.selectedDate;

      //  console.log("SSSFDFFF",$scope.selectedDate);
      //    console.log("SSkkkF",$scope.event.startDate);
      $scope.selectedYear = (selectedDate).getFullYear();
      $scope.selectedMonth = monthName[(selectedDate).getMonth()];
      $scope.selectedDay = (selectedDate).getDate();
      $scope.selectedWeek = weekName[(selectedDate).getDay()];

      $scope.handleEventChange();
    }

    // Get recurring text for the selection
    $scope.getRecurringText = function() {
      var whenValue = "";
      angular.forEach($scope.repeatEventOptions, function(repeatEvent, index) {
        if (repeatEvent.value == $scope.event.repeat) {
          whenValue = repeatEvent.text;
        }
      });

      return whenValue;
    }

    // Handle recurring change event
    $scope.handleRecurringChangeEvent = function(repeatEvent) {
      if (!$scope.event.startDate)
        return alert('Please select a valid start date');
      $scope.event.repeatEnd = '';
      $('#repeatEndDate').val("");
      $scope.repeatSelectValue = repeatEvent.text;
      $scope.event.repeat = repeatEvent.value;
      $scope.event.when = ($scope.event.repeat) ? $scope.getRecurringText() : "";
      if ($scope.event.repeat == 'never') {
        $scope.repeatEndDateFlag = false
      } else {
        $scope.event.endDate = '';
        $scope.repeatEndDateFlag = true;
      }
    }

    // Handle crete event
    $scope.createEvent = function() {
      $scope.evTitle = 0;
      $scope.evDescription = 0;
      $scope.evLocation = 0;

      if (!$scope.event.title)
        return alert('Please enter title of event.');

      if (!$scope.event.startDate)
        return alert('Please select a valid start date');

      if (!$scope.event.repeatEnd && !$scope.event.endDate)
        return alert('Please select a valid end date');


      var eventDates = [];
      var eventRepeatDates = [];

      if ($scope.event.endDate) {
        var end = new Date($scope.event.endDate);
        $scope.endUtcEpoc = Math.floor(end.getTime());
      } else if ($scope.event.repeatEnd) {
        var repeatEnd = new Date($scope.event.repeatEnd);
        $scope.repeatEndUtcEpoc = Math.floor(repeatEnd.getTime());
      }
      var start = new Date($scope.event.startDate);
      $scope.startUtcEpoc = Math.floor(start.getTime());

      if ($scope.event.repeat != 'never') {
        eventDates = (calculateRecurringDates[$scope.event.repeat](
          new Date($scope.event.startDate),
          new Date($scope.event.repeatEnd)
        ));

        for (var i = 0; i < eventDates.length; i++) {
          eventRepeatDates.push({
            "start": eventDates[i],
            "end": eventDates[i]
          });
        }
      } else {
        // $scope.endUtcEpoc = Math.floor(end.getTime() / 1000);

        eventRepeatDates.push({
          "start": $scope.startUtcEpoc,
          "end": $scope.endUtcEpoc
        });
      }

      /*save event in  database */
      var saveEvent = function() {
        var eventData = {
          "details": {
            "title": $scope.event.title,
            "description": $scope.event.description,
            "start": $scope.startUtcEpoc,
            "location": $scope.event.location,
            "images": $scope.event.images,
            //  "end"   : (($scope.event.repeat) ? $scope.event.repeatEnd : $scope.event.endDate),
            "end": (($scope.endUtcEpoc) ? $scope.endUtcEpoc : $scope.repeatEndUtcEpoc),
            "allday": (($scope.event.endDate) ? false : true),
          },
          "repeat": {
            "when": $scope.event.when,
            "frequency": eventRepeatDates.length,
            "reminder": 0,
            "dates": eventRepeatDates
          }
        }

        var url = '/unifydevents/events';
        $rootScope.callAPI(url, 'POST', eventData, function(res) {
          /* Data Successfully uploaded */
          if (res.data) {
            $scope.event = {};
            $scope.eventImage = '';
            $scope.graphicType = '';
            $('.closeBanner').hide();
            $('.closeCamera').show();
            $scope.selectedDateCalender = '';
            if (eventRepeatDates.length > 0) {
              $('#EventsiCal8EventsListData').remove();
              for (var i = 0; i < eventRepeatDates.length; i++) {

                var calenderObject = {
                  "Date": '',
                  "Title": '',
                  "Link": ''
                };
                var month = new Date(eventRepeatDates[i].start).getMonth();
                var year = new Date(eventRepeatDates[i].start).getFullYear();
                var day = new Date(eventRepeatDates[i].start).getDate();

                calenderObject.Date = new Date(year, month, day);
                calenderObject.Title = eventData.details.title;
                $scope.calenderList.push(calenderObject);
              }
              $("#calender").append('<div id="EventsiCal8EventsListData" class="displayBlockImp openEventCalendar border1px" style="display: block !important;"></div>')
              var setting = {}
              var element = document.getElementById('EventsiCal8EventsListData');
              caleandar(element, $scope.calenderList, setting);
            }
            alert("Event Added Successfully");
            $scope.dateByEvent();
          } else {
            alert("Event operation Failed");
            $scope.dateByEvent();

          }
        });

      }

      /*push image url in array and uplaod on server*/
      $scope.event.images = [];
      if ($scope.eventImage && $scope.graphicType === 'image') {
        $.blockUI();
        var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/imageUpload";
        var data = {
          'imageData': $scope.eventImage,
          "imageExtn": 'png'
        };

        $http.post(url, data).then(function(resp) {
          if (resp.data.url) {
            $scope.event.images.push(resp.data.url);
            saveEvent();
          } else {
            $.unblockUI();
          }
        });
      } else {
        saveEvent();
      }

    }

    $scope.fetchDayMonthYear();

  }

