  $scope.showResults = function() {
    if (!$scope.selectedtermCode) {
      showAlert("Select a term.");
      return;
    }
    if (!$scope.selectedsubjectCode && !$scope.selectedInstructorCode) {
      showAlert("Select a subject or instructor.");
      return;
    }
    $scope.showmessage = false;
    var mon, tue, wed, thu, fri, sat, sunday;
    mon = ($scope.weekdata[0].selected == 'Y') ? 'M' : '';
    tue = ($scope.weekdata[1].selected == 'Y') ? 'T' : '';
    wed = ($scope.weekdata[2].selected == 'Y') ? 'W' : '';
    thu = ($scope.weekdata[3].selected == 'Y') ? 'R' : '';
    fri = ($scope.weekdata[4].selected == 'Y') ? 'F' : '';
    sat = ($scope.weekdata[5].selected == 'Y') ? 'S' : '';
    sunday = ($scope.weekdata[6].selected == 'Y') ? 'U' : '';
    var startDate = "",
      endDate = "";
    var open_course = ($scope.showOpenCourse) ? 'Y' : 'N';
    var vday, vmonth, vyear;
    if ($('#startDate')[0].value) {
      var dt = new Date($('#startDate')[0].value);
      vday = dt.getDate();
      vday = (vday.toString().length == 1) ? "0" + vday : vday;
      vmonth = monthName[dt.getMonth()];
      vyear = dt.getFullYear();
      startDate = vday + "-" + vmonth + "-" + vyear;
    }
    if ($('#endDate')[0].value) {
      var dt = new Date($('#endDate')[0].value);
      vday = dt.getDate();
      vday = (vday.toString().length == 1) ? "0" + vday : vday;
      vmonth = monthName[dt.getMonth()];
      vyear = dt.getFullYear();
      endDate = vday + "-" + vmonth + "-" + vyear;
    }
    var searchURL = classSearchEndpoint + "/termCode=" + $scope.selectedtermCode +
      "&subjectCode=" + $scope.selectedsubjectCode +
      "&courseNumber=" + $scope.courseNumber +
      "&partTermCode=" + $scope.partTermCode +
      "&facultyPidm=" + $scope.selectedInstructorCode +
      "&courseTitle=" + $scope.selectedcourseCode +
      "&creditFrom=" + $scope.creditFrom +
      "&creditTo=" + $scope.creditTo +
      "&campCode=" + $scope.selectedcampusCode +
      "&opencourse=" + open_course +
      "&startDate=" + startDate +
      "&endDate=" + endDate +
      "&mon=" + mon +
      "&tue=" + tue +
      "&wed=" + wed +
      "&thu=" + thu +
      "&fri=" + fri +
      "&sat=" + sat +
      "&sun=" + sunday;
    searchURL = (demoAPI) ? "/bannerregCartclasslookup" : searchURL;
    $rootScope.getAPI(config, searchURL, function(data) {
      if (data.CLASSLOOKUP.length == 0) {
        showAlert("No classes found.");
      } else {
        console.log(data.CLASSLOOKUP);
        $scope.classes = [];
        $scope.inSearchWindow = false;
        var prevValue = "";
        var vindex = 0;
        angular.forEach(data.CLASSLOOKUP, function(value, key) {
          if (value[0].TIME && value[0].TIME.length > 8) {
            value[0].TIME_DISPLAY = value[0].SSRMEET_START_TIME + " - " + value[0].SSRMEET_END_TIME;
          } else {
            value[0].TIME_DISPLAY = "TBA";
          }

          value[0].START_DATE = "";
          value[0].END_DATE = "";
          value[0].ONLINE_COURSE = false;
          if ((value[0].SSRMEET_START_TIME == '' || value[0].SSRMEET_START_TIME == null) &&
            (value[0].SSRMEET_END_TIME == '' || value[0].SSRMEET_END_TIME == null) &&
            (value[0].TIME == "TBA")) {
            value[0].ONLINE_COURSE = true;
          }
          var days = value[0].TIME.indexOf(" ") != -1 ? value[0].TIME.split(" ")[0] : "";
          value[0].WEEK_DATA = "";
          value[0].WEEK_DATA += (days.indexOf("M") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Mon" : "";
          value[0].WEEK_DATA += (days.indexOf("T") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Tue" : "";
          value[0].WEEK_DATA += (days.indexOf("W") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Wed" : "";
          value[0].WEEK_DATA += (days.indexOf("R") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Thu" : "";
          value[0].WEEK_DATA += (days.indexOf("F") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Fri" : "";
          value[0].WEEK_DATA += (days.indexOf("S") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Sat" : "";
          value[0].WEEK_DATA += (days.indexOf("U") != -1) ? ((value[0].WEEK_DATA) ? ", " : "") + "Sun" : "";
          //if (value[0].WEEK_DATA) value[0].WEEK_DATA = value[0].WEEK_DATA.substring(0, value[0].WEEK_DATA.length - 1);

          if (!value[0].schedule) {
            value[0].schedule = [];
          }
          value[0].schedule.push({
            "DURATION": value[0].DURATION,
            "INSTRUCTOR": value[0].INSTRUCTOR,
            "TIME_DISPLAY": value[0].TIME_DISPLAY,
            "TIME": value[0].TIME,
            "LOCATION": value[0].LOCATION,
            "WEEK_DATA": value[0].WEEK_DATA
          });
          value[0].addedToCart = inCart(value[0].CRN);
          value[0].formatClass = "";
          value[0].REGISTERED = false;
          angular.forEach($scope.currentSchedule, function(schvalue, schkey) {
            if (schvalue.SFTREGS_CRN == value[0].CRN) {
              value[0].REGISTERED = true;
              //return;
            }
          });
          //
          if (prevValue == "") {
            $scope.classes.push(value[0]);
            prevValue = value[0];
          } else if (prevValue.CRN == value[0].CRN &&
            //prevValue.TERM_CODE == value[0].TERM_CODE &&
            //prevValue.SUBJECT_CODE == value[0].SUBJECT_CODE &&
            prevValue.CREDIT_HOURS == value[0].CREDIT_HOURS &&
            prevValue.COURSE_ID == value[0].COURSE_ID) {
            $scope.classes[vindex].schedule.push(value[0].schedule[0]);
          } else {
            vindex++;
            $scope.classes.push(value[0]);
            prevValue = value[0];
          }

        });
        $scope.page = 2;
        //$rootScope.appletName = "Courses";
      }
    });

  }

