    $('#endDate').val("");
    $scope.showOpenCourse = false;
  }

  $scope.addToCart = function(crn) {
    angular.forEach($scope.classes, function(value, key) {
      if (value.CRN == crn) {
        if (value.REMAINING <= 0) {
          return;
        }
        if (value.OLR_COURSE == 'Y') {
          if (!value.START_DATE && !value.END_DATE) {
            showAlert("Start Date or End Date should be mentioned for OLR courses.");
            return;
          }
          if (value.START_DATE && value.END_DATE) {
            showAlert("Only one of start or end dates may be entered.");
            return;
          }
          var cStartDate, cEndDate;
          if (value.START_DATE) {
            var dStartDate = new Date(value.START_DATE);
            cStartDate = dStartDate.getDate() + "-" + monthName[dStartDate.getMonth()] + "-" + dStartDate.getFullYear();
          }
          if (value.END_DATE) {
            var dEndDate = new Date(value.END_DATE);
            cEndDate = dEndDate.getDate() + "-" + monthName[dEndDate.getMonth()] + "-" + dEndDate.getFullYear();
          }
        }

        value.addedToCart = true;
        value.formatClass = "in-cart-course";
        $scope.cart.push(value);
        console.log($scope.cart);
        return;
      }
    });

  }

  $scope.removeFromCart = function(crn) {
    for (var i = 0; i <= $scope.cart.length - 1; i++) {
      if ($scope.cart[i].CRN === crn) {
        $scope.cart.splice(i, 1);
      }
    }
    angular.forEach($scope.classes, function(value, key) {
      if (value.CRN == crn) {
        value.addedToCart = false;
        value.formatClass = "";
        console.log($scope.cart);
      }
    });
  }

  $scope.gotoBack = function() {
    $scope.page = 1;
    //$rootScope.appletName = "Lookup Courses";
  }

  $scope.gotoCart = function() {
    $scope.page = 3;
    //$rootScope.appletName = "Cart";
  }

  $scope.gotoResultsPage = function() {
    $scope.page = 2;
    //$rootScope.appletName = "Courses";
  }

  $scope.submitCart = function() {
    if ($scope.cart.length == 0) {
      showAlert("No classes added to cart.");
      return;
    }
    //build param map for registration service
    var endpoint = "/banneraddDropClasses";
    var postdata = "";
    var termCode = "";
    var crns = "";
    var regCodes = "";
    var action = "A";
    var startDates = "";
    var endDates = "";
    var firstTime = true;
    $scope.regResults = "";

    angular.forEach($scope.cart, function(value, key) {
      termCode = $scope.selectedtermCode;
      if (!firstTime) {
        crns = crns + ",";
        regCodes = regCodes + ",";
        startDates = startDates + ",";
        endDates = endDates + ",";
      }
      crns = crns + value.CRN;
      regCodes = regCodes + "RW";
      if (value.START_DATE) {
        var dStartDate = new Date(value.START_DATE);
        cStartDate = (dStartDate.getDate() + 1) + "-" + monthName[dStartDate.getMonth()] + "-" + dStartDate.getFullYear();
        startDates = startDates + cStartDate;
      }
      if (value.END_DATE) {
        var dEndDate = new Date(value.END_DATE);
        cEndDate = (dEndDate.getDate() + 1) + "-" + monthName[dEndDate.getMonth()] + "-" + dEndDate.getFullYear();
        endDates = endDates + cEndDate;
      }
      firstTime = false;
    });
    postdata = "termCode=" + termCode + "&crns=" + crns + "&regCodes=" + regCodes + "&action=" + action + "&startDates=" + startDates + "&endDates=" + endDates;
    console.log(postdata);
    $rootScope.postAPI(config, endpoint, postdata, function(data) {
      if (typeof data.p_out[0].summary == "string") {
        var dres = JSON.parse(data.p_out[0].summary);
        data.p_out[0].summary = dres;
      }
      $scope.regResults = data.p_out[0];
      if ($scope.regResults.summary && $scope.regResults.summary.results) {
        angular.forEach($scope.regResults.summary.results, function(value, key) {
          if (value.status == 'S') {
            value.CSS_CLASS = "alert-success";
          } else if (value.status == 'E') {
            value.CSS_CLASS = "alert-danger";
          } else {
            value.CSS_CLASS = "alert-warning";
          }
        });
      }
      $scope.showmessage = true;
      $scope.clearCart();
      $scope.page = 4;
    });
  }

  $scope.gotoHomePage = function() {
    $scope.regResults = "";
    $scope.page = 1;
    //$rootScope.appletName = "Lookup Courses";
  }

  function initModule() {
    initScopeVars();
    initWeekData();
    //$rootScope.appletName = "Lookup Courses";
    $rootScope.getAPI(config, termdropdownURL, function(data) {
      $scope.termdropdown = data.termdropdown;
      $scope.clearCart();
      if (angular.isArray($scope.termdropdown) && $scope.termdropdown.length > 0) {
        
        if (autoSelectTerm == "on") {
         $scope.selectedtermCode = $scope.termdropdown[0].TERM_CODE;
         $scope.selectedterm = $scope.termdropdown[0].TERM_DESC;
         $scope.dropdownselect($scope.termdropdown[0]);
        }
      }
    });
  }

  initModule();

  /**
   * UI related tasks
   */
  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, $scope.title, null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: $scope.title,
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      } else {
        alert(msg);
      }
    }
  }

}

function pageprocessorNotification245(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.page = -1;
  $scope.last = true;


  $scope.messageDetail = function(index) {

    $scope.notificationList[index].shortMessage = $scope.notificationList[index].defaultMessage;
    $scope.notificationList[index].messageDetailLess = 1;


  }

  $scope.messageDetailLess = function(index) {
    $scope.notificationList[index].shortMessage = $scope.notificationList[index].defaultMessage.slice(0, 100);
    $scope.notificationList[index].messageDetailLess = 0;

  }
  $scope.loadPage = function(page) {
    page++;
    //var url = '/unifydmessaging//messaging/search/findByGroupsInOrIndividualUserInAndGenderIn?sort=createdDate,DESC&size=5&page=' + page;
    var url = '/unifydmessaging/userNotifications?size=5&page=' + page;

    $rootScope.callAPI(url, 'GET', {}, function(res) {

      try {

        $scope.page = res.data.number;
        $scope.last = res.data.last;
        var notificationColl = [];
        angular.forEach(res.data.content, function(value,key){
          notificationColl.push({
            "id": value.id,
            "email": value.email,
            "msgId": value.msgId,
            "requireAcknowlegement": value.requireAcknowlegement,
            "acknowleged": value.acknowleged,
            "groups": value.messaging.groups,
            "individualUser": value.messaging.individualUser,
            "gender": value.messaging.gender,
            "userRole": value.messaging.userRole,
            "title": value.messaging.title,
            "defaultMessage": value.messaging.defaultMessage,
            "graphicType": value.messaging.graphicType,
            "graphicUrl": value.messaging.graphicUrl,
            "channels": value.messaging.channels,
            "tags": value.messaging.tags,
            "channelMsg": value.messaging.channelMsg,
            "scheduledMsg": value.messaging.scheduledMsg,
            "scheduleTime": value.messaging.scheduleTime,
            "important": value.messaging.important,
            "createdDate": value.messaging.createdDate,
            "createdBy": value.messaging.createdBy
          });
        });
        console.log(notificationColl);
        if (!$scope.notificationList) {
          $scope.notificationList = notificationColl;

          $scope.notificationList = $scope.notificationList.map(function(value, key) {
            value.shortMessage = value.defaultMessage.slice(0, 100);
            value.messageDetailLess = 0;
            return value;
          });

        } else {
          $scope.notificationList = $scope.notificationList.concat(notificationColl);

          $scope.notificationList = $scope.notificationList.map(function(value, key) {
            value.shortMessage = value.defaultMessage.slice(0, 100);
            value.messageDetailLess = 0;
            return value;
          });
        }


      } catch (err) {
        $scope.notificationList = [];
      }
    });
  };

  $scope.loadPage($scope.page);

}

