              $scope.filterSiteTrafficDuration = 'Yesterday';
              $scope.getSiteTraffic('yesterday', 'today')
              break;
            }
          case '7Days':
            {
              $scope.filterSiteTrafficDuration = 'Last 7 days';
              $scope.getSiteTraffic('7daysAgo', 'today');
              break;
            }
          case '14Days':
            {
              $scope.filterSiteTrafficDuration = 'Last 14 days';
              $scope.getSiteTraffic('14daysAgo', 'today')
              break;
            }
