              $scope.filterSiteTrafficDuration = 'Last 28 days';
              $scope.getSiteTraffic('28daysAgo', 'today')
              break;
            }
        }
      }

      $scope.getSiteTraffic = function(stDt, edDt) {
        gapi.client.analytics.data.ga.get({
            'ids': 'ga:' + profileId,
            'start-date': stDt,
            'end-date': edDt,
            'metrics': 'ga:sessions,ga:users,ga:bounceRate,ga:avgSessionDuration'
          })
          .then(function(response) {
            //var formattedJson = JSON.stringify(response.result, null, 2);
            //console.log(formattedJson);
            $scope.analyticsInfo = response.result;
            $scope.sessions = $scope.analyticsInfo.totalsForAllResults["ga:sessions"];
            $scope.users = $scope.analyticsInfo.totalsForAllResults["ga:users"];
            $scope.bounceRate = $scope.analyticsInfo.totalsForAllResults["ga:bounceRate"];
            $scope.avgSessionDuration = $scope.analyticsInfo.totalsForAllResults["ga:avgSessionDuration"];
            $scope.bounceRateDisplay = ($scope.bounceRate) ? parseFloat($scope.bounceRate).toFixed(2) + "%" : "";
            $scope.avgSessionDurationDisplay = "";
            if ($scope.avgSessionDuration) {
              var minutes = Math.floor($scope.avgSessionDuration / 60);
              var secs = Math.floor($scope.avgSessionDuration - minutes * 60);
              $scope.avgSessionDurationDisplay = (minutes == 0) ? "" : minutes + "m" + " ";
              $scope.avgSessionDurationDisplay += " " + (secs) ? secs + "s" : "";
            }
            console.log($scope.analyticsInfo);
            console.log($scope.sessions);
            console.log($scope.users);
            try {
              $scope.$apply();
            } catch (ex) {
              console.error(ex);
            }

            $('.showSiteTraffic').removeClass('ng-hide').addClass('ng-show');
            $('.showSiteTrafficInfoLoader').removeClass('ng-show').addClass('ng-hide');

            var dataChart1 = new gapi.analytics.googleCharts.DataChart({
              query: {
                'ids': 'ga:' + profileId, // <-- Replace with the ids value for your view.
                'start-date': stDt,
                'end-date': edDt,
                'metrics': 'ga:sessions,ga:users',
                'dimensions': 'ga:date'
              },
              chart: {
                'container': 'siteTrafficChart',
                'type': 'LINE',
                'options': {
                  'width': '100%',
                  'pieSliceText': 'percentage',
                }
              }
            });
            /*dataChart1.on('success', function(response) {
               setTimeout(function(){
                $('.showSiteTraffic').removeClass('ng-hide').addClass('ng-show');
                $('.showSiteTrafficInfoLoader').removeClass('ng-show').addClass('ng-hide');
               },500) 
            });*/

            dataChart1.execute();
          })
          .then(null, function(err) {
            // Log any errors.
            console.log(err);
          });
      }

      $scope.filterSiteTraffic('7Days');


      /**
       * Creates a new DataChart instance showing top 5 most popular demos/tools
       * amongst returning users only.
       * It will be rendered inside an element with the id "chart-3-container".
       */
      $('.showPageView').removeClass('ng-show').addClass('ng-hide');
      $('.showPageViewInfoLoader').removeClass('ng-hide').addClass('ng-show');

      $scope.filterPageView = function(filterParam) {
        $('.showPageView').removeClass('ng-show').addClass('ng-hide');
        $('.showPageViewInfoLoader').removeClass('ng-hide').addClass('ng-show');

        switch (filterParam) {
          case 'Today':
            {
              $scope.filterPageViewDuration = 'Today';
              $scope.getTopPages('today', 'today')
              break;
            }
          case 'Yesterday':
            {
              $scope['filterPageViewDuration'] = 'Yesterday';
              $scope.getTopPages('yesterday', 'today')
              break;
            }
          case '7Days':
            {
              $scope.filterPageViewDuration = 'Last 7 days';
              $scope.getTopPages('7daysAgo', 'today');
              break;
            }
          case '14Days':
            {
              $scope.filterPageViewDuration = 'Last 14 days';
              $scope.getTopPages('14daysAgo', 'today')
              break;
            }
          case '28Days':
            {
              $scope.filterPageViewDuration = 'Last 28 days';
              $scope.getTopPages('28daysAgo', 'today')
              break;
            }
        }
      }

      $scope.getTopPages = function(stDt, edDt) {
        var dataChart2 = new gapi.analytics.googleCharts.DataChart({
          query: {
            'ids': 'ga:' + profileId, // <-- Replace with the ids value for your view.
            'start-date': stDt,
            'end-date': edDt,
            'metrics': 'ga:pageviews',
            'dimensions': 'ga:pagePathLevel1',
            'sort': '-ga:pageviews',
            'filters': 'ga:pagePathLevel1!=/',
            'max-results': 100
