          chart: {
            'container': 'chart-2-container',
            'type': 'TABLE',
            'options': {
              'width': '100%',
              'page': 'enable',
              'pageSize': 8
                //'pieHole': 4 / 9,
            }
          }
        });

        dataChart2.on('success', function(response) {
          $('.showPageView').removeClass('ng-hide').addClass('ng-show');
          $('.showPageViewInfoLoader').removeClass('ng-show').addClass('ng-hide');
        });

        dataChart2.on('error', function(response) {
          console.log('analytics error' + response);
        });



        dataChart2.execute();
      }

      $scope.filterPageView('7Days');




      $('.showDeviceInfo').removeClass('ng-show').addClass('ng-hide');
      $('.showDeviceInfoLoader').removeClass('ng-hide').addClass('ng-show');

      $scope.filterDevice = function(filterParam) {
        $('.showDeviceInfo').removeClass('ng-show').addClass('ng-hide');
        $('.showDeviceInfoLoader').removeClass('ng-hide').addClass('ng-show');

        switch (filterParam) {
