              $scope.getTopdevices('today', 'today')
              break;
            }
          case 'Yesterday':
            {
              $scope['deviceFilterDuration'] = 'Yesterday';
              $scope.getTopdevices('yesterday', 'today')
              break;
            }
          case '7Days':
            {
              $scope.deviceFilterDuration = 'Last 7 days';
              $scope.getTopdevices('7daysAgo', 'today');
              break;
            }
          case '14Days':
