              $scope.deviceFilterDuration = 'Last 14 days';
              $scope.getTopdevices('14daysAgo', 'today')
              break;
            }
          case '28Days':
            {
              $scope.deviceFilterDuration = 'Last 28 days';
              $scope.getTopdevices('28daysAgo', 'today')
              break;
            }
        }
      }

      $scope.getTopdevices = function(stDt, edDt) {
        var topDevicesChart = new gapi.analytics.googleCharts.DataChart({
          query: {
            'ids': 'ga:' + profileId, // <-- Replace with the ids value for your view.
            'start-date': stDt,
            'end-date': edDt,
            'metrics': 'ga:sessions',
            'dimensions': 'ga:deviceCategory'
          },
          chart: {
            'container': 'chart-3-container',
            'type': 'PIE',
            'options': {
              'width': '100%',
              'pieSliceText': 'percentage',
              'colors': ['#4285f4', '#45a5f5', '#93d5ed'],
              'pieHole': 0.5,
              'hAxis': {
                textPosition: 'none'
              }
            }
          }
        });

        topDevicesChart.on('success', function(response) {
          $('.showDeviceInfo').removeClass('ng-hide').addClass('ng-show');
          $('.showDeviceInfoLoader').removeClass('ng-show').addClass('ng-hide');
          $scope.topDevicesChartInfo = response.data.rows;
          $scope.totalDeviceHits = ($scope.topDevicesChartInfo[0] != undefined ? $scope.topDevicesChartInfo[0].c[1].v : 0) + ($scope.topDevicesChartInfo[1] != undefined ? $scope.topDevicesChartInfo[1].c[1].v : 0) + ($scope.topDevicesChartInfo[2] != undefined ? $scope.topDevicesChartInfo[2].c[1].v : 0);
          console.log(response.data.rows);
          console.log($scope.totalDeviceHits);
          $scope.$apply();
        });

        topDevicesChart.on('error', function(response) {
          console.log('analytics error' + response);
        });

        topDevicesChart.execute();
      }

      $scope.filterDevice('7Days');


      /*var dataChart4 = new gapi.analytics.googleCharts.DataChart({
          query: {
              'ids': 'ga:' + profileId, // <-- Replace with the ids value for your view.
              'start-date': 'yesterday',
              'end-date': 'today',
              'metrics': 'ga:sessions,ga:users',
              'dimensions': 'ga:date'
          },
          chart: {
              'container': 'chart-4-container',
              'type': 'LINE',
              'options': {
                  'width': '100%',
                  'pieSliceText': 'percentage'
              }
          }
      });

      dataChart4.execute();*/


      $('.showCountryInfo').removeClass('ng-show').addClass('ng-hide');
      $('.showCountryInfoLoader').removeClass('ng-hide').addClass('ng-show');

      $scope.filterCountry = function(filterParam) {
