function pageprocessorAcademics4(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $scope.classes = [];
  var classObj = {
    "course": "",
    "credits": "",
    "grade": "",
    "classGPA": ""
  }
  $scope.classes.push(classObj);
  $rootScope.back = function() {
    $rootScope.leftToRight = true;
    $location.path("/app/RegistrationStatus/RegistrationStatus");
    //window.location.href = "index.html#app/Menu82/Menu82";
  }

  /* Assign configuration params values to local variables */
  try {
    // $scope.gradeCode = JSON.parse(pagedef.gradeCodes);
    $scope.gradeCodes = pagedef.quickdialnumbers;
    // console.log($scope.gradeCodes);
  } catch (err) {
    $scope.gradeCodes = [];
    showAlert("You must correctly enter the Grade codes !");
  }

  /*
   Grade codes example:
   [{ "Grade": "A", "Grade_point": 4.0},
   { "Grade": "A-","Grade_point": 3.67},
   { "Grade": "B+","Grade_point": 3.33},
   { "Grade": "B", "Grade_point": 3.0 },
   { "Grade": "B-","Grade_point": 2.67},
   { "Grade": "C+","Grade_point": 2.33},
   { "Grade": "C", "Grade_point": 2.0 },
   { "Grade": "C-","Grade_point": 1.67},
   { "Grade": "D+","Grade_point": 1.33},
   { "Grade": "D", "Grade_point": 1.0 },
   { "Grade": "F", "Grade_point": 0   }]
   */

  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, "GPA Calculator", null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: 'GPA Calculator',
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      } else {
        alert(msg);
      }

    }
  }




  $scope.cumGPA = "";
  $scope.cumCredits = "";
  $scope.cumulativeGPA = 0;
  $scope.semesterGPA = 0;

  $scope.getGradePoint = function(grade) {
    var gp = 0;
    angular.forEach($scope.gradeCodes, function(value, key) {
      if (value.grade == grade) {
        gp = value.grade_point;
      }
    });
    return gp;
  }

  $scope.calculateGPA = function() {
    $scope.semesterGPA = 0;
    $scope.cumulativeGPA = 0;
    var totcredits = 0;
    var totgradeval = 0;
    var totgpaVal = 0;
    var nval = 0;
    var ngrade = 0;
    var ngpoint = 0;
    console.log("you are here");
    angular.forEach($scope.classes, function(value, key) {
      nval = 0;
      ngrade = 0;
      if (value.credits && !isNaN(value.credits)) {
        console.log("credits=" + value.credits + " - " + isNaN(value.credits));
        try {
          nval = parseInt(value.credits);
        } catch (ex) {
          nval = 0;
        }
        if (value.grade) {
          ngrade = $scope.getGradePoint(value.grade);
        }
        ngpoint = nval * ngrade;
        console.log("ngpoint=" + ngpoint);
        if (ngpoint > 0) {
          value.classGPA = ngpoint;
        } else {
          value.classGPA = "0";
        }
        console.log("still here");
        totcredits += nval;
        totgradeval += ngpoint;
      } else if (value.credits == "0") {
        console.log("credits zero");
        value.classGPA = "0";
      }
    });
    console.log("totcredits=" + totcredits);
    console.log("totgradeval=" + totgradeval);
    if (totcredits > 0) {
      totgpaVal = totgradeval / totcredits;
    } else {
      totgpaVal = 0;
    }
    $scope.semesterGPA = totgpaVal;
    // $scope.cumulativeGPA = 0;
    if (!isNaN($scope.cumGPA) && !isNaN($scope.cumCredits)) {
      totgradeval += ($scope.cumGPA * $scope.cumCredits);
      totcredits += Number($scope.cumCredits);
      $scope.cumulativeGPA = totgradeval / totcredits;

      if (isNaN($scope.cumulativeGPA)) {
        $scope.cumulativeGPA = 0
      }
    }

    console.log("checking value semester==>" + $scope.semesterGPA)
    console.log("checking value cumulative==>" + $scope.cumulativeGPA)

  }

  $scope.gradedropdownselect = function(grade) {
    console.log("dropdown clicked");
    console.log("grade=" + grade);
    //MM:$scope.calculateGPA();
  }

  $scope.addNewClass = function() {
    var classObj = {
      "course": "",
      "credits": "",
      "grade": "",
      "classGPA": ""
    }
    $scope.classes.push(classObj);

  }

  $scope.removeRow = function(index) {
    $scope.classes.splice(index, 1);
    //recalculate gpa
    //MM:$scope.calculateGPA();
  }


  $scope.minusCredits = function(myclass) {
    if (myclass.credits) {
      if (!isNaN(myclass.credits)) {
        try {
          var myInt = parseInt(myclass.credits);
          myInt--;
          if (myInt < 0) {
            myInt = 0;
          }
          myclass.credits = myInt;
        } catch (ex) {
          myclass.credits = 0;
        }
      } else {
        myclass.credits = 0;
      }
    } else {
      myclass.credits = 0;
    }
    $scope.calculateGPA();
  }

  $scope.plusCredits = function(myclass) {
    if (myclass.credits == "0" || myclass.credits) {
      if (!isNaN(myclass.credits)) {
        try {
          var myInt = parseInt(myclass.credits);
          myInt++;

          myclass.credits = myInt;
        } catch (ex) {
          myclass.credits = 0;
        }
      } else {
        myclass.credits = 0;
      }
    } else {
      myclass.credits = 0;
    }
    $scope.calculateGPA();
  }

  $scope.changeCredit = function(myclass) {
    if (myclass.credits == "0" || myclass.credits) {
      if (!isNaN(myclass.credits)) {
        try {
          var myInt = parseInt(myclass.credits);
          console.log(myInt);
          if (myInt < 0) {
            console.log("resetting value back to 0");
            myclass.credits = 0;
          }
        } catch (ex) {
          //
        }
      }
    }
    //MM:$scope.calculateGPA();
  }

  $scope.validateInteger = function(field) {
    var item;
    console.log(field);
    if (field == 'cumGPA') {
      item = $scope.cumGPA;
    } else if (field == 'cumCredits') {
      item = $scope.cumCredits;
    }
    console.log("item=" + item);
    if (item == "0" || item) {
      if (!isNaN(item)) {
        try {
          var myInt = parseInt(item);
          console.log(myInt);
          if (myInt < 0) {
            console.log("resetting value back to 0");
            if (field == 'cumGPA') {
              $scope.cumGPA = 0;
            } else if (field == 'cumCredits') {
              $scope.cumCredits = 0;
            }
          }
          if (item > 100) {
            console.log("resetting value back to 100");
            if (field == 'cumGPA') {
              $scope.cumGPA = 100;
            } else if (field == 'cumCredits') {
              $scope.cumCredits = 100;
            }
          }
        } catch (ex) {
          //
        }
      }
    }
    //MM:$scope.calculateGPA();
  }

  $scope.plusCumulative = function(item) {
    var vfield = 0;
    if (item == 'cumgpa') {
      vfield = $scope.cumGPA;
    } else if (item == 'cumcredits') {
      vfield = $scope.cumCredits;
    } else {
      return;
    }

    if (vfield == "0" || vfield) {
      if (!isNaN(vfield)) {
        try {
          var myInt = parseFloat(vfield);
          myInt++;

          vfield = myInt;
        } catch (ex) {
          vfield = 0;
        }
      } else {
        vfield = 0;
      }
    } else {
      vfield = 0;
    }

    if (item == 'cumgpa') {
      $scope.cumGPA = vfield;
    } else if (item == 'cumcredits') {
      $scope.cumCredits = vfield;
    }
    $scope.calculateGPA();
  }

  $scope.minusCumulative = function(item) {
    var vfield = 0;
    if (item == 'cumgpa') {
      vfield = $scope.cumGPA;
    } else if (item == 'cumcredits') {
      vfield = $scope.cumCredits;
    } else {
      return;
    }

    if (vfield) {
      if (!isNaN(vfield)) {
        try {
          var myInt = parseFloat(vfield);
          myInt--;
          if (myInt < 0) {
            myInt = 0;
          }
          vfield = myInt;
        } catch (ex) {
          vfield = 0;
        }
      } else {
        vfield = 0;
      }
    } else {
      vfield = 0;
    }
    if (item == 'cumgpa') {
      $scope.cumGPA = vfield;
    } else if (item == 'cumcredits') {
      $scope.cumCredits = vfield;
    }
    $scope.calculateGPA();
  }

  $scope.focusstatus = "";
  $('.page-container').on("focusout", '.event-control', function() {
    console.log("focusout called");
    $("#footerContentContainer").addClass('footerGroupIcons').removeClass('hideFooter');
    $("#bottomPoweredBy").removeClass('hideFooter');
    $scope.focusstatus = "focusout";
  });

  $('.page-container').on("focusin", '.event-control', function() {
    console.log("focusin called");
    $("#footerContentContainer").addClass('hideFooter').removeClass('footerGroupIcons');
    $("#bottomPoweredBy").addClass('hideFooter');
    $scope.focusstatus = "focusin";
  });


}

