function pageprocessorWeatherOrNot(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  console.log($routeParams);
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(function(position) {
      var prelat = position.coords.latitude.toString();
      var prelong = position.coords.longitude.toString();
      var glat = prelat.substring(0, 6);
      var glong = prelong.substring(0, 6);
      var fullURL = '' + pagedef.endpoint + '' + glat + '&lon=' + glong + '&appid=' + pagedef.apiKey;
	  $http.get(fullURL).then(function(response) {
        $("#loader").hide();
        $("#loaded").show();
        $scope.weather = response.data.weather[0];
        $scope.latt = glat;
        $scope.long = glong;
        $scope.sys = response.data.sys;
        $scope.location = response.data.name;
        $scope.main = response.data.main;
      });
	  var cb1 = function(loaded) {
	      var myOptions = {
	        zoom: 12,
	        mapTypeId: google.maps.MapTypeId.HYBRID
	      };
	      var mapDisplay = document.getElementById("map")
	      var mapheight = "200"
	      $("#map").css("height", mapheight + "px");
	      var map = new google.maps.Map(mapDisplay, myOptions);
	      map.setTilt(45);
	      if (navigator.geolocation) {
	        navigator.geolocation.getCurrentPosition(function(position) {
	          var pos = new google.maps.LatLng(position.coords.latitude, position.coords.longitude);

	          var infowindow = new google.maps.InfoWindow({
	            map: map,
	            position: pos,
	            content: $scope.location
	          });
	          map.setCenter(pos);
	        }, function() {
	          //handleNoGeolocation(true);
	        }, {
	          timeout: 10000,
	          frequency: 3000,
	          enableHighAccuracy: true
	        });
	      }
	    }
	    window.cb1 = cb1;
	    if (typeof google === 'object' && typeof google.maps === 'object') {
	      window.cb1(true);
	    } else {
	     $.getScript("https://maps.googleapis.com/maps/api/js?key=AIzaSyDUbP_Qxh9wbPOvgW8iH_zuLu8K3fSlv0I&sensor=false&callback=cb1");
	    }
    });
  }
}

