      message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
    });
    navigator.camera.getPicture($scope.cameraSuccess, onFail, {
      quality: 50,
      destinationType: Camera.DestinationType.DATA_URL,
      sourceType: Camera.PictureSourceType.PHOTOLIBRARY,
      popoverOptions: new CameraPopoverOptions(300, 300, 100, 100, Camera.PopoverArrowDirection.ARROW_ANY),
      correctOrientation: true
    });

  }

  var ActionSheetSuccess = function(btn) {
    if (btn == 3) {
      return;
    } else if (btn == 1) {
      $scope.useCamera();
    } else {
      $scope.useGallery();
    }
  };

  $scope.openCamera = function() {


    var options = {
      'androidTheme': window.plugins.actionsheet.ANDROID_THEMES.THEME_HOLO_LIGHT,
      'title': '',
      'buttonLabels': ['Take a photo', 'Choose from gallery'],
      'addCancelButtonWithLabel': 'Cancel'
    };
    window.plugins.actionsheet.show(options, ActionSheetSuccess);
  };




  $scope.uploadProfileImage = function() {

    $(".app").css("position", "static");
    if ($(".app-body").hasClass("animated")) {
      $(".app-body").removeClass("animated");
    }


    if (window.device) {
      $scope.openCamera();
    } else {
      console.log('filechooser');
      try {
        $("#uploadProfileImg").change(function() {

          if (this.files && this.files[0]) {

            if (this.files[0].name.split('.').pop().toLowerCase() == 'png' || this.files[0].name.split('.').pop().toLowerCase() == 'jpg' || this.files[0].name.split('.').pop().toLowerCase() == 'jpeg') {

              var FR = new FileReader();
              FR.onload = function(e) {

                $scope.targetImage = e.target.result;

                //Set image width before cropping (modal-image)
                $scope.getmodalCropUrl = $(".ModalCropImage").attr('src');
                var winWidth = $(window).width();

                if (winWidth <= 549) {
                  $("#profileCropModal .cropModalContent").width(winWidth - 13);
                }

                var Readerimg = new Image();
                Readerimg.src = e.target.result;

                Readerimg.onload = function() {
                  var ReaderimgWid = this.width;

                  if (ReaderimgWid < 550) {
                    $scope.ModalCropImage = "";
                    $scope.ModalCropImage = e.target.result;
                    $scope.$apply();
                  } else {
                    $scope.addCropModal();
                  }

                }
              };

              if (this.files[0].size > 500000) {

                if (window.device) {
                  navigator.notification.alert("Image size is too large");
                } else {
                  alert("Image size is too large");
                }
                return;
              }
              FR.readAsDataURL(this.files[0]);
              $("#uploadProfileImg")[0].value = '';
            } else {
              if (window.device) {
                navigator.notification.alert("Only png, jpg file types are allowed.", null, '', 'Ok');
              } else {
                alert("Only png, jpg file types are allowed.");
              }
            }
          }
        });

        document.getElementById("uploadProfileImg").click();
      } catch (e) {
        alert(e);
      }

    }
  };
  //Resize and Crop Starts(Robin)	
  var image = new Image();

  $scope.addCropModal = function() {

    $("#profileCropModal").modal("show");

    var getModalWidth = $("#profileCropModal .cropModalContent").width();

    image.src = $scope.targetImage;

    var modalCanvas = document.getElementById("modalCanvas");
    var ctx = modalCanvas.getContext("2d");

    modalCanvas.width = getModalWidth - 22;
    modalCanvas.height = 350;

    image.onload = function(e) {
      ctx.drawImage(image,
        0, 0, image.width, image.height,
        0, 0, modalCanvas.width, modalCanvas.height
      );

      var createModalImage = new Image();
      createModalImage.src = modalCanvas.toDataURL();

      $scope.ModalCropImage = createModalImage.src;
      $scope.$apply();
    };
  }

  try{
  var container = $(".ModalcropBody");
  $("#canvas").resizable({
    containment: container
  }).draggable({
    containment: container
  });
}catch(e){}
  var getBase64 = "";
  var canvas = document.getElementById('Canvasss');
  var context = canvas.getContext('2d');
  var imageObj = new Image();

  $scope.profileCrop = function() {

      imageObj.src = $(".ModalCropImage").attr("src");

      var imgWidth = $("#canvas").width();
      var imgHeight = $("#canvas").height();
      var imgPos = $("#canvas").position();

      var sourceX = imgPos.left;
      var sourceY = imgPos.top;

      var sourceWidth = imgWidth;
      var sourceHeight = imgHeight;

      var destWidth = 146;
      var destHeight = 146;

      var destX = 0;
      var destY = 0;

      context.drawImage(imageObj, sourceX, sourceY, sourceWidth, sourceHeight, destX, destY, destWidth, destHeight);

      var getBase64 = canvas.toDataURL();
      imageObj.src = getBase64;
      $scope.currentUser.profileImage = getBase64;
      $scope.newProfileImage = getBase64;

      console.log($scope.currentUser.profileImage);
      $scope.ModalCropImage = "";
      $scope.$apply();

      $scope.addPosition();

    }
    //Resize and Crop End


  $('#profileCropModal').modal({
    backdrop: 'static',
    keyboard: false,
    show: false
  });




  $scope.uploadProfileCover = function() {
    if (window.device) {
      $scope.openCamera();
    } else {
      console.log('filechooser');
      try {
        $("#uploadProfileCvr").change(function() {
          if (this.files && this.files[0]) {
            if (this.files[0].name.split('.').pop().toLowerCase() == 'png' || this.files[0].name.split('.').pop().toLowerCase() == 'jpg' || this.files[0].name.split('.').pop().toLowerCase() == 'jpeg') {
              var FR = new FileReader();
              FR.onload = function(e) {
                console.log(e.target.result);
                $scope.newProfileBackground = e.target.result;
                //$('#profileImg').attr("src", $scope.newProfileImage);

                $scope.currentUser.profileBackground = e.target.result;

                $scope.$apply();
              };


              console.log("size", this.files);

              if (this.files[0].size > 500000) {


                if (window.device) {
                  navigator.notification.alert("Image size is too large");
                } else {
                  alert("Image size is too large");
                }
                return;
              }
              if (this.files[0].size < 100000) {


                if (window.device) {
                  navigator.notification.alert("Image size is too small");
                } else {
                  alert("Image size is too small");
                }
                return;
              }
              FR.readAsDataURL(this.files[0]);
              $("#uploadProfileCvr")[0].value = '';
            } else {
              if (window.device) {
                navigator.notification.alert("Only png, jpg file types are allowed.", null, '', 'Ok');
