  function updateUserProfile(callBack) {
    url = '/unifydidentity/user';
    $rootScope.callAPI(url, 'POST', $scope.currentUser, function(res) {
      $.unblockUI();
      if (!res.data) {
        if (window.device) {
          navigator.notification.alert(
            'Unable to register user.', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('Unable to register user.');
        }
        return callBack("Error");
      }
      var data = res.data;
      $rootScope.user['id'] = data['id'];
      $rootScope.user['profileBackground'] = data['profileBackground'];
      $rootScope.user['firstName'] = data['firstName'];
      $rootScope.user['lastName'] = data['lastName'];
      $rootScope.user['profileImage'] = data['profileImage'];
      $rootScope.user['role'] = data['role'];
      $rootScope.user['tenant'] = data['tenant'];
      $rootScope.user['email'] = data['email'];
      $rootScope.user['phone'] = data['phone'];
      $rootScope.user['areaOfInterest'] = data['areaOfInterest'];
      $rootScope.user['channels'] = data['channels'];
      return callBack();
    });
  }

  $scope.updateProfileData = function() {

    console.log("Selected Profile Background:");

    if ($scope.newProfileImage.length != 0) {
      $scope.currentUser.profileImage = $scope.newProfileImage;
      $scope.currentUser.profileBackground = $scope.currentUser.profileBackground;
      $scope.isDirty = true;
    }

    var pref = [];
    for (var i = 0; i < $scope.areaOfInterest.length; i++) {
      if ($scope.areaOfInterest[i].selected)
        pref.push($scope.areaOfInterest[i].id);
    }
    if (pref.length == 0) {
      var msg = "Please select atleast one preference";
      showAlert(msg);
      return;
    }

    console.log(pref);
    $scope.currentUser.areaOfInterest = pref;
    $rootScope.user['areaOfInterest'] = pref;
    if (!$scope.isDirty) {
      return;
    }
    //Saving
    console.log("Updating Profile Data...");
    try {
      console.log($scope.newProfileImage.length);


      if ($scope.newProfileImage.length != 0) {
        $.blockUI();
        var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/imageUpload";
        var data = {
          'imageData': $scope.newProfileImage,
          "imageExtn": 'png'
        };
        $http.post(url, data).then(function(resp) {
          if (resp.data.url) {
            $scope.currentUser.profileImage = resp.data.url;
            console.log("Profile Image Url: " + $scope.currentUser.profileImage);
            updateUserProfile(function(err) {
              if (!err) {
                $scope.editMode = false;
                $scope.isDirty = false;
                $scope.newProfileImage = "";
                $scope.currentUserTemp = $scope.currentUser;
              }
            });
            //$scope.createNewPost();
          } else {
            console.log("Error in uploading image to kryptosda");
            $.unblockUI();
          }
        });
      } else {
        console.log("No Updates to profileImage");
        updateUserProfile(function(err) {
          console.log("Profile updated !");
          if (!err) {
            $scope.editMode = false;
            $scope.isDirty = false;
            $scope.newProfileImage = "";
            $scope.currentUserTemp = $scope.currentUser;
          }
        });
      }
    } catch (e) {
      alert(e)
    }
  }


  $rootScope.callAPI(url, 'GET', {}, function(res) {
    $.unblockUI();
    if (!res.data && res.status != 200) {
      if (window.device) {
        navigator.notification.alert(
          'Unable to login, please try again', // message
          null, // callback
          'Error', // title
          'OK' // buttonName
        );
      } else {
        alert('Unable to login, please try again')
      }
      return;
    }
    $scope.currentUser = res.data;
    //$scope.currentUser.role = ["Adminstrator","Tester","Moderator"];
    console.log(res.data);
    $scope.currentUser.profileBackground = ($scope.currentUser.profileBackground) ? ($scope.currentUser.profileBackground) : "#525252";
    console.log("selected color: " + $scope.currentUser.profileBackground);
    try {
      $("#colorPalette").spectrum({
        color: $scope.currentUser['profileBackground'],
        showPaletteOnly: true,
        change: function(color) {
          console.log("sel color: callApi " + color);
          try {
            $scope.currentUser['profileBackground'] = color.toHexString();
            console.log('profileBackground ' + $scope.currentUser['profileBackground']);
          } catch (e) {
            console.log(e);
          }
          $scope.isDirty = true;
        },
        palette: [
          ['rgb(0, 74, 209)', 'rgb(255, 172, 1)', 'rgb(209, 48, 144)', 'rgb(117, 67, 148)'],
          ['rgb(218, 194, 41)', 'rgb(54, 179, 126)', 'rgb(255, 86, 48)', 'rgb(81, 82, 82)'],
          ['rgb(37, 39, 41)', 'rgb(255, 242, 0)', 'rgb(0, 204, 62)', 'rgb(0, 174, 239)']
        ]
      });
    } catch (e) {}
    try {
      $scope.$apply();
    } catch (ex) {
      console.log(ex);
    }
    if ($scope.currentUser.firstName && $scope.currentUser.lastName) {
      $scope.currentUser.title = $scope.currentUser.firstName.substr(0, 1) + $scope.currentUser.lastName.substr(0, 1);
    }
    url = '/unifyedcategory/category';
    $.blockUI();
    $rootScope.callAPI(url, 'GET', {}, function(res) {
      $.unblockUI();
      var data = res.data;
      console.log('category ' + JSON.stringify(data));

      $scope.areaOfInterest = data['_embedded']['category'];

      for (var i = 0; i < $scope.areaOfInterest.length; i++) {
        $scope.areaOfInterest[i].selected = false;
        angular.forEach($scope.currentUser.areaOfInterest, function(value, key) {
          if (value == $scope.areaOfInterest[i].id) {
            $scope.areaOfInterest[i].selected = true;
          }
        });
      }
      console.log("areas of interests:");
      console.log($scope.areaOfInterest)
    });
  });

  $scope.preselected = function(index) {
    if (!$scope.areaOfInterest[index].enable)
      return;

    $scope.isDirty = true;
    $scope.areaOfInterest[index].selected = !$scope.areaOfInterest[index].selected;
    if ($scope.areaOfInterest[index].selected)
      $scope.selectedCount++;
    else
      $scope.selectedCount--;

  };

  $scope.cancelEdit = function() {
    $scope.currentUser.profileImage = "";
    $scope.currentUser['profileBackground'] = $scope.currentUserTemp['profileBackground'];
    $scope.currentUser['profileImage'] = $scope.currentUserTemp['profileImage'];
    $scope.currentUser['areaOfInterest'] = $scope.currentUserTemp['areaOfInterest'];
    $scope.currentUser['profileImageCover'] = $scope.currentUserTemp['profileImageCover'];


    console.log(JSON.stringify($scope.currentUserTemp));
    console.log(JSON.stringify($scope.currentUser));
    $scope.editMode = false;
    $scope.isDirty = false;
    $scope.newProfileImage = "";
    for (var i = 0; i < $scope.areaOfInterest.length; i++) {
      $scope.areaOfInterest[i].selected = false;
      angular.forEach($scope.currentUser.areaOfInterest, function(value, key) {
        if (value == $scope.areaOfInterest[i].id) {
          $scope.areaOfInterest[i].selected = true;
        }
      });
    }
    console.log($scope.currentUser);
    $scope.$apply();
  }

}

function pageprocessorMenu82(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $rootScope.loggedIn ? $(".header").show() : $(".header").hide();

  $rootScope.back = function() {
    $location.path("/amakerhome");
  }

  $("#fixedFooterContent").html('');
  $rootScope.appDisplayName = $rootScope.mobileAppName;
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $.jStorage.get('tenantInfo').erp ? $scope.showLoggedInIcon = true : $scope.showLoggedInIcon = false;
  $rootScope.appletSearch = "";
  var data2 = '<div class="proximaFont borderNone listItemCustomPadding rounded-0" style="background-color: #252729 !important;padding:0px 18px 8px 18px;"><div class="hamburgerSearchBox"><input class="form-control needsclick" ng-model="$root.appletSearch" placeholder="Search Apps" style="background: transparent;border: none;color: #fff;padding: 6px 6px;" type="text" /></div></div>';
  var htmlView = $compile($(data2))($scope);

  //var btm = $('.footerGroupIcons').height();
  $('#appContent').css('padding-bottom', 5 + 'px');

  $scope.logout = function() {
    try {
      $scope.confirm = function(btn) {
        if (btn == 1) {
          $.jStorage.deleteKey("user");
          $.jStorage.deleteKey("unifyedusername");
          $.jStorage.deleteKey("unifyedpassword");
          $rootScope.user = null;
          $rootScope.loggedIn = false;
          $location.path("/app/SignIn279/SignIn279");

        } else {
          //$location.path("/home");
        }
        $scope.$apply();
      }
      if (window.device)
        navigator.notification.confirm("Are you sure you want to logout?", $scope.confirm, 'Logout', ['Yes', 'No']);
      else {
        var retVal = confirm("Do you want to continue ?");
        if (retVal == true) {
          $.jStorage.set("userdetails", null);
          $location.path("/app/SignIn279/SignIn279");
          $.jStorage.deleteKey("UserProfile");

          $.jStorage.deleteKey("profileData");
        } else {

        }
      }
    } catch (e) {
      alert(e)
    }
  }

  $scope.byAppName = function(appname) {
    try {
      return function(app) {
        return app.displayname.indexOf(appname) != -1
      }
    } catch (e) {
      alert(e)
    }
  }


  $scope.$on("$destroy", function() {
    $(".header").show();
    $("#fixedHeaderContent").html("");
  });

  $('input:text').focusout(function() {
    $("#footerContentContainer").addClass('footerGroupIcons').removeClass('hideFooter');
    $("#bottomPoweredBy").removeClass('hideFooter');

  });

  $('input:text').focusin(function() {
    $("#footerContentContainer").addClass('hideFooter').removeClass('footerGroupIcons');
    $("#bottomPoweredBy").addClass('hideFooter');
  });

  $scope.loginPage = function() {
    $location.path("/app/SignIn279/SignIn279");
  }

  $scope.checkUserRole = function(roles) {
    if (roles) {
      return roles.split(',').some(function(role) {
        return $rootScope.user.role.includes(role);
      });
    } else {
      return true;
    }
  }
}

function pageprocessorKyleTest(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  console.log($routeParams);
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(function(position) {
      var prelat = position.coords.latitude.toString();
      var prelong = position.coords.longitude.toString();
      var glat = prelat.substring(0, 6);
      var glong = prelong.substring(0, 6);
      var fullURL = '' + pagedef.endpoint + '' + glat + '&lon=' + glong + '&appid=' + pagedef.apiKey;
      $http.get(fullURL).then(function(response) {
        $("#loader").hide();
        $("#loaded").show();
        $scope.weather = response.data.weather[0];
        $scope.latt = glat;
        $scope.long = glong;
        $scope.sys = response.data.sys;
        $scope.location = response.data.name;
        $scope.main = response.data.main;
      });
      var cb1 = function(loaded) {
        var myOptions = {
          zoom: 12,
          mapTypeId: google.maps.MapTypeId.HYBRID
        };
        var mapDisplay = document.getElementById("map")
        var mapheight = "200"
        $("#map").css("height", mapheight + "px");
        var map = new google.maps.Map(mapDisplay, myOptions);
        map.setTilt(45);
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(function(position) {
            var pos = new google.maps.LatLng($scope.latt, $scope.long);

            var infowindow = new google.maps.InfoWindow({
              map: map,
              position: pos,
              content: $scope.location
            });
            map.setCenter(pos);
          }, function() {
            //handleNoGeolocation(true);
          }, {
            timeout: 10000,
            frequency: 3000,
            enableHighAccuracy: true
          });
        }
      }
      window.cb1 = cb1;
      if (typeof google === 'object' && typeof google.maps === 'object') {
        window.cb1(true);
      } else {
        $.getScript("https://maps.googleapis.com/maps/api/js?key=AIzaSyDUbP_Qxh9wbPOvgW8iH_zuLu8K3fSlv0I&sensor=false&callback=cb1");
      }
    });
  }
}

